import React, { createContext, useState, useEffect, useContext } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const ClientContext = createContext();

export const ClientProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // SweetAlert2 config
  const Toast = Swal.mixin({
    toast: true,
    position: "top-end",
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.onmouseenter = Swal.stopTimer;
      toast.onmouseleave = Swal.resumeTimer;
    },
  });

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all clients
  const fetchClients = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("clients/");
      setClients(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new client
  const createClient = async (clientData) => {
    try {
      const response = await axiosInstance.post("clients/", clientData);
      setClients((prevClients) => [...prevClients, response.data]);
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Success",
        text: "Client created successfully!",
        timer: 2000, // Auto-close after 2 seconds
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a client
  const updateClient = async (clientId, updatedFields) => {
    try {
      const response = await axiosInstance.put(
        `clients/${clientId}/`,
        updatedFields
      );
      setClients((prevClients) =>
        prevClients.map((client) =>
          client.id === clientId ? response.data : client
        )
      );
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Updated",
        text: "Report updated successfully!",
        timer: 2000,
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a client
  const deleteClient = async (clientId) => {
    try {
      await axiosInstance.delete(`clients/${clientId}/`);
      setClients((prevClients) =>
        prevClients.filter((client) => client.id !== clientId)
      );
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Deleted",
        text: "Client deleted successfully!",
        timer: 2000,
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Fetch clients when user logs in or changes
  useEffect(() => {
    if (user) fetchClients();
    else setClients([]); // Clear clients if no user
  }, [user]);

  return (
    <ClientContext.Provider
      value={{
        clients,
        selectedClient,
        setSelectedClient,
        createClient,
        updateClient,
        deleteClient,
        loading,
        error,
      }}
    >
      {children}
    </ClientContext.Provider>
  );
};

// Custom hook for accessing the ClientContext
export const useClientContext = () => useContext(ClientContext);
