import { useState } from "react";

// Contexts
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/ReportModal.css";

/* Could be put into a JSON file */
// BRAND AND MODEL
const brandModelMap = {
  Toyota: ["Corolla", "Camry", "Yaris"],
  Ford: ["Focus", "Mustang", "F-150"],
  Honda: ["Civic", "Accord", "CR-V"],
};
const brands = Object.keys(brandModelMap);

const CreateOrEditVehicleModal = ({ onClose, onDelete }) => {
  const {
    selectedVehicle,
    setSelectedVehicle,
    createVehicleWithAlert,
    updateVehicleWithAlert,
  } = useVehicleContext();
  const { owners } = useOwnerContext();

  const [vehicleData, setVehicleData] = useState({
    brand: selectedVehicle?.brand || "",
    model: selectedVehicle?.model,
    year: selectedVehicle?.year || "pending",
    license_plate: selectedVehicle?.license_plate || "",
    owner: selectedVehicle?.owner || "",
  });

  const handleVehicleChange = (e) => {
    const { name, value } = e.target;

    setVehicleData({
      ...vehicleData,
      [name]: name === "owner" ? Number(value) : value,
    });
  };
  const handleBrandChange = (e) => {
    const { value } = e.target;
    setVehicleData({
      ...vehicleData,
      brand: value,
      model: "", // Reset model when brand changes
    });
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    if (!vehicleData.brand) {
      Toast.fire("Error", "Please select a brand.", "error");
      return;
    }
    if (!vehicleData.model) {
      Toast.fire("Error", "Please select a model.", "error");
      return;
    }
    if (!vehicleData.year) {
      Toast.fire("Error", "Please select a year.", "error");
      return;
    }
    if (!vehicleData.license_plate) {
      Toast.fire("Error", "Please fill in a license plate.", "error");
      return;
    }
    if (!vehicleData.owner) {
      Toast.fire("Error", "Please select an owner.", "error");
      return;
    }

    try {
      const newVehicle = await createVehicleWithAlert(vehicleData);
      if (newVehicle) {
        setVehicleData({
          brand: "",
          model: "",
          year: "",
          license_plate: "",
          owner: "",
        });
      }
    } catch (error) {
      console.error("Error creating report:", error);
      Toast.fire("Error", "Something went wrong.", "error");
    } finally {
      onClose();
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!vehicleData.brand) {
      Toast.fire("Error", "Please select a brand.", "error");
      return;
    }
    if (!vehicleData.model) {
      Toast.fire("Error", "Please select a model.", "error");
      return;
    }
    if (!vehicleData.year) {
      Toast.fire("Error", "Please select a year.", "error");
      return;
    }
    if (!vehicleData.license_plate) {
      Toast.fire("Error", "Please fill in a license plate.", "error");
      return;
    }
    if (!vehicleData.owner) {
      Toast.fire("Error", "Please select an owner.", "error");
      return;
    }

    try {
      const updatedVehicle = await updateVehicleWithAlert(
        selectedVehicle.id,
        vehicleData
      );
    } catch (error) {
      console.error("Error updating vehicle:", error);
      Toast.fire("Error", "Something went wrong.", "error");
    } finally {
      setSelectedVehicle(null);
      onClose();
    }
  };

  return (
    <div className="modal-container">
      <div className="modal-card">
        <svg
          onClick={onClose}
          className="modal-card-close"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
        >
          <path
            width="100%"
            height="100%"
            d="M11.414 10l2.829-2.828a1 1 0 1 0-1.415-1.415L10 8.586 7.172 5.757a1 1 0 0 0-1.415 1.415L8.586 10l-2.829 2.828a1 1 0 0 0 1.415 1.415L10 11.414l2.828 2.829a1 1 0 0 0 1.415-1.415L11.414 10zM10 20C4.477 20 0 15.523 0 10S4.477 0 10 0s10 4.477 10 10-4.477 10-10 10z"
          />
        </svg>
        {selectedVehicle ? <h2>Edit Vehicle</h2> : <h2>Create Vehicle</h2>}
        <form
          className="modal-form"
          onSubmit={selectedVehicle ? handleEditSubmit : handleCreateSubmit}
        >
          <fieldset className="form-inputs">
            <legend>Create New Vehicle</legend>
            <label>
              Brand:
              <select
                name="brand"
                value={vehicleData.brand}
                onChange={handleBrandChange}
                required
              >
                <option value="">Select a brand</option>
                {brands.map((brand) => (
                  <option key={brand} value={brand}>
                    {brand}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Model:
              <select
                name="model"
                value={vehicleData.model}
                onChange={handleVehicleChange}
                disabled={!vehicleData.brand}
                required
              >
                <option value="">Select a model</option>
                {vehicleData.brand &&
                  brandModelMap[vehicleData.brand]?.map((model) => (
                    <option key={model} value={model}>
                      {model}
                    </option>
                  ))}
              </select>
            </label>
            <label>
              Year:
              <input
                type="number"
                name="year"
                placeholder="Enter vehicle year"
                value={vehicleData.year}
                onChange={handleVehicleChange}
                min="1900"
                max={new Date().getFullYear()}
                required
              />
            </label>
            <label>
              License plate:
              <input
                type="text"
                name="license_plate"
                placeholder="Enter vehicle license plate"
                value={vehicleData.license_plate}
                onChange={handleVehicleChange}
                required
              />
            </label>
            <label>
              Owner:
              <select
                name="owner"
                value={vehicleData.owner}
                onChange={handleVehicleChange}
              >
                <option value="">Select an owner</option>
                {owners.map((owner) => (
                  <option key={owner.id} value={owner.id}>
                    {owner.full_name}
                  </option>
                ))}
              </select>
            </label>
          </fieldset>
          <div className="button-group">
            {selectedVehicle ? (
              <button type="submit">Update Vehicle</button>
            ) : (
              <button type="submit">Create Vehicle</button>
            )}
            {selectedVehicle && (
              <button type="button" className="delete-btn" onClick={onDelete}>
                Delete
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};
export default CreateOrEditVehicleModal;
