import React from "react";
import Modal from "../components/Modal";
import { useReportContext } from "../contexts/ReportContext";

const ViewReportModal = ({ report, onClose }) => {
  if (!report) return null;

  return (
    <Modal onClose={onClose}>
      <div className="auth-container">
        <div className="auth-card">
          <h2>Report Details</h2>

          <div className="report-details">
            <p>
              <strong>Vehicle:</strong> {report.vehicle_name}
            </p>
            <p>
              <strong>Owner:</strong> {report.owner_name}
            </p>
            <p>
              <strong>Status:</strong> {report.status}
            </p>
            <p>
              <strong>Remarks:</strong> {report.remarks || "No remarks"}
            </p>
            <p>
              <strong>Created At:</strong>{" "}
              {new Date(report.created_at).toLocaleString()}
            </p>
          </div>

          <h3>Repairs</h3>
          {report.repairs.length > 0 ? (
            <ul className="repair-list">
              {report.repairs.map((repair, index) => (
                <li key={index}>
                  {repair.description} - â‚¬{repair.total_cost}
                </li>
              ))}
            </ul>
          ) : (
            <p>No repairs recorded.</p>
          )}

          <div className="button-group">
            <button type="button" className="cancel-btn" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export default ViewReportModal;
