import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useUserContext } from "../contexts/UserContext";
import {
  isValidUsername,
  isValidEmail,
  isValidPassword,
  passwordsMatch,
} from "../utils/validation";
import axios from "axios";
import Swal from "sweetalert2";
import "../styles/Auth.css";

const Register = () => {
  const { users } = useUserContext();
  const navigate = useNavigate();

  // Form fields
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // Validation states
  const [usernameError, setUsernameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");

  // Check validation on input change
  useEffect(() => {
    if (username) {
      setUsernameError(
        isValidUsername(username, users)
          ? ""
          : "Username is taken or too short (min 3 chars)."
      );
    }
    if (email) {
      setEmailError(
        isValidEmail(email, users)
          ? ""
          : "Invalid email format or already in use."
      );
    }
    if (password) {
      setPasswordError(
        isValidPassword(password)
          ? ""
          : "Password must be at least 8 chars, contain a number and an uppercase letter."
      );
    }
    if (confirmPassword) {
      setConfirmPasswordError(
        passwordsMatch(password, confirmPassword)
          ? ""
          : "Passwords do not match."
      );
    }
  }, [username, email, password, confirmPassword, users]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Send registration data to the backend
      await axios.post("http://localhost:8000/api/register/", {
        username,
        email,
        password,
      });

      // Show success message
      Swal.fire({
        icon: "success",
        title: "Registration Successful",
        text: "You have been registered successfully! Please log in.",
      });

      // Redirect to the login page
      navigate("/login");
    } catch (error) {
      console.error("Registration failed:", error);
      Swal.fire({
        icon: "error",
        title: "Registration Failed",
        text:
          error.response?.data?.error ||
          "An error occurred during registration. Please try again.",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Register</h2>
        <form onSubmit={handleSubmit} className="auth-form">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className={usernameError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{usernameError && <>{usernameError}</>}</p>

          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={emailError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{emailError && <>{emailError}</>}</p>

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={passwordError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{passwordError && <>{passwordError}</>}</p>

          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className={confirmPasswordError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">
            {confirmPasswordError && <>{confirmPasswordError}</>}
          </p>

          <button
            type="submit"
            disabled={
              !!usernameError ||
              !!emailError ||
              !!passwordError ||
              !!confirmPasswordError ||
              loading
            }
          >
            {loading ? "Registering..." : "Register"}
          </button>
        </form>
        <p className="bottomline">
          Already have an account? <Link to="/login">Log In</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
