import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

const BASE_URL = "http://localhost:8000";

export const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Helper function to get headers with token
  const getHeaders = () => {
    const token = localStorage.getItem("access_token");
    if (!token) {
      setError("User is not authenticated");
      return null;
    }
    return {
      Authorization: `Bearer ${token}`,
    };
  };

  // Fetch reports from the server
  const fetchReports = async () => {
    const headers = getHeaders();
    if (!headers) return;

    try {
      setLoading(true);
      const response = await axios.get(`${BASE_URL}/api/reports/`, { headers });
      setReports(response.data);
    } catch (err) {
      console.error("Error fetching reports:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      const response = await axios.post(`${BASE_URL}/api/reports/`, reportData);
      setReports([...reports, response.data]);
    } catch (error) {
      console.error("Error creating report:", error);
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedFields) => {
    try {
      // Find the existing report by ID
      const existingReport = reports.find((report) => report.id === reportId);

      if (!existingReport) {
        console.error("Report not found");
        return;
      }

      // Merge updatedFields with existingReport
      const payload = {
        ...existingReport,
        ...updatedFields,
      };

      // Send the payload to the API
      const response = await axios.put(
        `http://localhost:8000/api/reports/${reportId}/`,
        payload
      );

      // Update the state with the new report data
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );

      console.log("Report updated successfully:", response.data);
    } catch (error) {
      console.error("Error updating report:", error);
      console.error("Response data:", error.response?.data || error.message);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    try {
      await axios.delete(`${BASE_URL}/api/reports/${reportId}/`);
      setReports(reports.filter((r) => r.id !== reportId));
    } catch (error) {
      console.error("Error deleting report:", error);
    }
  };

  useEffect(() => {
    fetchReports(); // Fetch reports when the component mounts
  }, []);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        fetchReports,
        createReport,
        updateReport,
        deleteReport,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};
