import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

// Styles
import "../styles/CreateItemButton.css"; // Create a CSS file for styling

const CreateItemButton = ({ itemType, handleCreateClick }) => {
  const location = useLocation();
  const [visible, setVisible] = useState(false);
  const [shouldRender, setShouldRender] = useState(false);

  // Define the paths where the button should be visible
  const allowedPaths = [
    "/report",
    "/owner",
    "/vehicle",
    "/inventory",
    "/tasktemplate",
  ];

  useEffect(() => {
    if (allowedPaths.includes(location.pathname)) {
      setShouldRender(true); // Ensure the button is mounted
      setTimeout(() => setVisible(true), 10); // Small delay to trigger animation
    } else {
      setVisible(false); // Start slide-out animation
      const timer = setTimeout(() => setShouldRender(false), 300); // Wait before unmounting

      return () => clearTimeout(timer); // Cleanup on unmount
    }
  }, [location.pathname]);

  if (!shouldRender) return null; // Prevent rendering until needed

  return (
    <div
      className={`create-item-button-container${
        visible ? " slide-in" : "slide-out"
      }`}
    >
      <button
        title={`Create new ${itemType}`}
        type="button"
        className="create-item-button"
        onClick={handleCreateClick}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="currentColor"
          className="icon"
        >
          <path
            d="M12 22.4199C17.5228 22.4199 22 17.9428 22 12.4199C22 6.89707 17.5228 2.41992 12 2.41992C6.47715 2.41992 2 6.89707 2 12.4199C2 17.9428 6.47715 22.4199 12 22.4199Z"
            strokeWidth="1.5"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path d="M12 8V16" strokeWidth="1.5" strokeLinecap="round" />
          <path d="M8 12H16" strokeWidth="1.5" strokeLinecap="round" />
        </svg>
      </button>
    </div>
  );
};

export default CreateItemButton;
