import React, { useState } from "react";
import { Toast } from "../utils/sweetalert";
// Components
import FilterBar from "../components/FilterBar";
import CreateReportModal from "../components/CreateReportModal";
import ViewReportModal from "../components/ViewReportModal";
import Modal from "../components/Modal";
// Styles
import "../styles/Reports.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useUserContext } from "../contexts/UserContext";
import { useReportContext } from "../contexts/ReportContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useVehicleContext } from "../contexts/VehicleContext";

const Reports = () => {
  const { authenticatedUser } = useAuth();
  const { getUserNameById } = useUserContext();
  const {
    reports,
    selectedReport,
    setSelectedReport,
    updateReport,
    deleteReport,
  } = useReportContext();
  const { owners, getOwnerNameByVehicleId } = useOwnerContext();
  const { vehicles, getVehicleInfoByVehicleId } = useVehicleContext();
  const [filters, setFilters] = useState({
    vehicle_id: "",
    user_id: "",
    created_at: "",
    owner: "",
    status: "",
  });
  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredReports = reports.filter((report) => {
    // Find the vehicle associated with this report
    const vehicle = vehicles.find((v) => v.id === report.vehicle_id);
    // Get the owner ID from the vehicle (if it exists)
    const ownerId = vehicle ? vehicle.owner_id : null;

    return (
      (filters.vehicle_id === "" ||
        report.vehicle_id?.toString() === filters.vehicle_id) &&
      (filters.user_id === "" ||
        report.user_id?.toString() === filters.user_id) &&
      (filters.created_at === "" ||
        report.formatted_created_at.includes(filters.created_at)) &&
      (filters.owner === "" || ownerId?.toString() === filters.owner) &&
      (filters.status === "" || report.status === filters.status)
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(true);
  };
  const handleEditClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleEditSubmit = (updatedReport) => {
    updateReport(selectedReport.id, updatedReport);
    setShowEditModal(false);
    setSelectedReport(null);
  };
  const handleDeleteConfirm = () => {
    deleteReport(selectedReport.id);
    setShowDeleteModal(false);
    setSelectedReport(null);
  };

  // Return a truncated text
  const truncateText = (text, maxLength = 25) => {
    return text.length > maxLength ? text.slice(0, maxLength) + "..." : text;
  };

  return (
    <div className="reports-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "vehicle_id",
            label: "Vehicle",
            value: filters.vehicle_id,
            type: "select",
          },
          {
            name: "created_at",
            label: "Created Date",
            value: filters.created_at,
            type: "select",
          },
          {
            name: "owner",
            label: "Owner",
            value: filters.owner,
            type: "select",
          },
          {
            name: "user_id",
            label: "Created By",
            value: filters.user_id,
            type: "select",
          },
          {
            name: "status",
            label: "Status",
            value: filters.status,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Reports */}
      <div className="reports-list">
        {filteredReports.map((report) => (
          <div
            key={report.id}
            className="report-card"
            onClick={() => handleViewClick(report)}
          >
            <div className="status">
              {report.status === "pending" ? (
                <svg
                  className="status-icon pending"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="currentColor"
                >
                  <path d="M12 7.25C12.4142 7.25 12.75 7.58579 12.75 8V13C12.75 13.4142 12.4142 13.75 12 13.75C11.5858 13.75 11.25 13.4142 11.25 13V8C11.25 7.58579 11.5858 7.25 12 7.25Z" />
                  <path d="M12 17C12.5523 17 13 16.5523 13 16C13 15.4477 12.5523 15 12 15C11.4477 15 11 15.4477 11 16C11 16.5523 11.4477 17 12 17Z" />
                  <path d="M8.2944 4.47643C9.36631 3.11493 10.5018 2.25 12 2.25C13.4981 2.25 14.6336 3.11493 15.7056 4.47643C16.7598 5.81544 17.8769 7.79622 19.3063 10.3305L19.7418 11.1027C20.9234 13.1976 21.8566 14.8523 22.3468 16.1804C22.8478 17.5376 22.9668 18.7699 22.209 19.8569C21.4736 20.9118 20.2466 21.3434 18.6991 21.5471C17.1576 21.75 15.0845 21.75 12.4248 21.75H11.5752C8.91552 21.75 6.84239 21.75 5.30082 21.5471C3.75331 21.3434 2.52637 20.9118 1.79099 19.8569C1.03318 18.7699 1.15218 17.5376 1.65314 16.1804C2.14334 14.8523 3.07658 13.1977 4.25818 11.1027L4.69361 10.3307C6.123 7.79629 7.24019 5.81547 8.2944 4.47643ZM9.47297 5.40432C8.49896 6.64148 7.43704 8.51988 5.96495 11.1299L5.60129 11.7747C4.37507 13.9488 3.50368 15.4986 3.06034 16.6998C2.6227 17.8855 2.68338 18.5141 3.02148 18.9991C3.38202 19.5163 4.05873 19.8706 5.49659 20.0599C6.92858 20.2484 8.9026 20.25 11.6363 20.25H12.3636C15.0974 20.25 17.0714 20.2484 18.5034 20.0599C19.9412 19.8706 20.6179 19.5163 20.9785 18.9991C21.3166 18.5141 21.3773 17.8855 20.9396 16.6998C20.4963 15.4986 19.6249 13.9488 18.3987 11.7747L18.035 11.1299C16.5629 8.51987 15.501 6.64148 14.527 5.40431C13.562 4.17865 12.8126 3.75 12 3.75C11.1874 3.75 10.4379 4.17865 9.47297 5.40432Z" />
                </svg>
              ) : report.status === "in_progress" ? (
                <svg
                  className="status-icon in-progress"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="currentColor"
                >
                  <path
                    d="M11.9426 1.25H12.0574C14.3658 1.24999 16.1748 1.24998 17.5863 1.43975C19.031 1.63399 20.1711 2.03933 21.0659 2.93414C21.9607 3.82895 22.366 4.96897 22.5603 6.41371C22.75 7.82519 22.75 9.63423 22.75 11.9426V12.0574C22.75 14.3658 22.75 16.1748 22.5603 17.5863C22.366 19.031 21.9607 20.1711 21.0659 21.0659C20.1711 21.9607 19.031 22.366 17.5863 22.5603C16.1748 22.75 14.3658 22.75 12.0574 22.75H11.9426C9.63423 22.75 7.82519 22.75 6.41371 22.5603C4.96897 22.366 3.82895 21.9607 2.93414 21.0659C2.03933 20.1711 1.63399 19.031 1.43975 17.5863C1.24998 16.1748 1.24999 14.3658 1.25 12.0574V11.9426C1.24999 9.63423 1.24998 7.82519 1.43975 6.41371C1.63399 4.96897 2.03933 3.82895 2.93414 2.93414C3.82895 2.03933 4.96897 1.63399 6.41371 1.43975C7.82519 1.24998 9.63423 1.24999 11.9426 1.25ZM6.61358 2.92637C5.33517 3.09825 4.56445 3.42514 3.9948 3.9948C3.42514 4.56445 3.09825 5.33517 2.92637 6.61358C2.75159 7.91356 2.75 9.62177 2.75 12C2.75 14.3782 2.75159 16.0864 2.92637 17.3864C3.09825 18.6648 3.42514 19.4355 3.9948 20.0052C4.56445 20.5749 5.33517 20.9018 6.61358 21.0736C7.91356 21.2484 9.62177 21.25 12 21.25C14.3782 21.25 16.0864 21.2484 17.3864 21.0736C18.6648 20.9018 19.4355 20.5749 20.0052 20.0052C20.5749 19.4355 20.9018 18.6648 21.0736 17.3864C21.2484 16.0864 21.25 14.3782 21.25 12C21.25 9.62177 21.2484 7.91356 21.0736 6.61358C20.9018 5.33517 20.5749 4.56445 20.0052 3.9948C19.4355 3.42514 18.6648 3.09825 17.3864 2.92637C16.0864 2.75159 14.3782 2.75 12 2.75C9.62177 2.75 7.91356 2.75159 6.61358 2.92637ZM12 7.25C12.4142 7.25 12.75 7.58579 12.75 8V11.6893L15.0303 13.9697C15.3232 14.2626 15.3232 14.7374 15.0303 15.0303C14.7374 15.3232 14.2626 15.3232 13.9697 15.0303L11.8358 12.8964C11.5468 12.6074 11.4022 12.4629 11.3261 12.2791C11.25 12.0954 11.25 11.891 11.25 11.4822V8C11.25 7.58579 11.5858 7.25 12 7.25Z"
                    fill="#1C274C"
                  />
                </svg>
              ) : (
                <img
                  className="status-icon completed"
                  src="images/check-circle.svg"
                  alt="Completed"
                />
              )}
            </div>
            <div className="report-infos">
              <h3>
                {truncateText(getVehicleInfoByVehicleId(report.vehicle_id))}
              </h3>
              <p>
                <strong>Created Date:</strong>&nbsp;
                {report.formatted_created_at}
              </p>
              <p>
                <strong>Owner:</strong>&nbsp;
                {getOwnerNameByVehicleId(report.vehicle_id)}
              </p>
              <p>
                <strong>Status:</strong>&nbsp;
                <span
                  style={{
                    color:
                      report.status === "pending"
                        ? "red"
                        : report.status === "in_progress"
                        ? "orange"
                        : "green",
                  }}
                >
                  {report.status_display}
                </span>
              </p>
              <p>
                <strong>Created By:</strong>&nbsp;
                {getUserNameById(report.user_id)}
              </p>
            </div>
            <div className="report-actions">
              {authenticatedUser.id === report.user_id && (
                <>
                  <button
                    className="btn btn-green"
                    onClick={() => handleEditClick(report)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-red"
                    onClick={() => handleDeleteClick(report)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
        {filteredReports.length === 0 && <p>No reports match your filters.</p>}
      </div>

      {/* Floating New Report Button */}
      <button className="btn btn-float" onClick={handleCreateClick}>
        New Report
      </button>

      {/* Create Report Modal */}
      {showCreateModal && (
        <CreateReportModal onClose={() => setShowCreateModal(false)} />
      )}

      {/* View Report Modal */}
      {showViewModal && (
        <ViewReportModal onClose={() => setShowViewModal(false)} />
      )}

      {/* Edit Report Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();

              const title = e.target.title.value.trim();
              const description = e.target.description.value.trim();
              const createdDate = e.target.created_date.value;
              const owner = e.target.owner.value;

              // Validation
              if (title.length < 3) {
                Toast.fire(
                  "Error",
                  "Title must be at least 3 characters long.",
                  "error"
                );
                return;
              }
              if (description.length < 10) {
                Toast.fire(
                  "Error",
                  "Description must be at least 10 characters long.",
                  "error"
                );
                return;
              }
              if (!createdDate) {
                Toast.fire("Error", "Please select a creation date.", "error");
                return;
              }
              if (!owner) {
                Toast.fire("Error", "Please select a valid owner.", "error");
                return;
              }

              const updatedReport = {
                title: title,
                description: description,
                created_date: createdDate,
                owner: owner,
              };
              handleEditSubmit(updatedReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Owner:
                <select name="owner" defaultValue={selectedReport.owner}>
                  {owners.map((owner) => (
                    <option key={owner.id} value={owner.id}>
                      {owner.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowEditModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Report Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this report?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Reports;
