import Modal from "./Modal";

const CreateReportModal = ({ onClose, onSubmit, owners, user }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const newReport = {
      title: e.target.title.value,
      description: e.target.description.value,
      created_date: e.target.created_date.value,
      created_by: user.id,
      client: e.target.client.value,
    };
    onSubmit(newReport);
  };

  return (
    <Modal onClose={onClose}>
      <form onSubmit={handleSubmit}>
        <fieldset className="form-inputs">
          <legend>Create New Report</legend>
          <label>
            Title:
            <input type="text" name="title" placeholder="Enter report title" />
          </label>
          <label>
            Description:
            <textarea name="description" placeholder="Enter description" />
          </label>
          <label>
            Created Date:
            <input
              type="date"
              name="created_date"
              defaultValue={new Date().toISOString().split("T")[0]}
            />
          </label>
          <label>
            Owner:
            <select name="client">
              <option value="">Select Owner</option>
              {owners.map((owner) => (
                <option key={owner.id} value={owner.id}>
                  {owner.name}
                </option>
              ))}
            </select>
          </label>
        </fieldset>
        <div className="form-actions">
          <button className="btn btn-green" type="submit">
            Save
          </button>
          <button className="btn btn-blue" type="button" onClick={onClose}>
            Cancel
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default CreateReportModal;
