import React, { createContext, useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() =>
    localStorage.getItem("token")
      ? JSON.parse(localStorage.getItem("authTokens")).access
      : null
  );
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Check for a token in localStorage on initial load
  useEffect(() => {
    const fetchUserData = async (token) => {
      try {
        const response = await axios.get("http://localhost:8000/api/user/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data); // Set user data
      } catch (error) {
        console.error("Failed to fetch user data:", error);
        logout(); // Log out if the token is invalid
      }
    };

    const token = localStorage.getItem("token");
    if (token) {
      fetchUserData(token); // Fetch user data using the token
    }
  }, []);

  const login = async (email, password) => {
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/api/login/", {
        email,
        password,
      });
      localStorage.setItem("token", response.data.access);
      localStorage.setItem("refreshToken", response.data.refresh);
      const fetchUserData = async (token) => {
        const userResponse = await axios.get(
          "http://localhost:8000/api/user/",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setUser(userResponse.data); // Set user data
      };
      await fetchUserData(response.data.access);
      Swal.fire({
        icon: "success",
        title: "Login Successful",
        text: "You have been logged in successfully!",
      });
      return true;
    } catch (error) {
      console.error("Login failed:", error);
      Swal.fire({
        icon: "error",
        title: "Login Failed",
        text: "Invalid email or password. Please try again.",
      });
      return false;
    } finally {
      setLoading(false); // Stop loading
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("refreshToken");
    setUser(null);
    Swal.fire({
      icon: "success",
      title: "Logout Successful",
      text: "You have been logged out successfully!",
    });
    navigate("/login");
  };

  const refreshToken = async () => {
    try {
      const refreshToken = localStorage.getItem("refreshToken");
      const response = await axios.post(
        "http://localhost:8000/api/token/refresh/",
        { refresh: refreshToken }
      );
      localStorage.setItem("token", response.data.access);
      setUser({ token: response.data.access });
    } catch (error) {
      console.error("Token refresh failed:", error);
      logout();
    }
  };

  return (
    <AuthContext.Provider
      value={{ user, login, logout, refreshToken, loading }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
