// Contexts
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/ReportModal.css";

const ViewVehicleModal = ({ onClose, onEdit, onDelete }) => {
  const { selectedVehicle, setSelectedVehicle } = useVehicleContext();
  return (
    <form
      className="modal-form"
      onSubmit={(e) => {
        e.preventDefault();
      }}
    >
      <fieldset className="form-inputs" disabled>
        <legend>View Vehicle</legend>
        <label>
          Brand:
          <input
            type="text"
            name="brand"
            defaultValue={selectedVehicle.brand}
          />
        </label>
        <label>
          Model:
          <textarea
            type="email"
            name="model"
            defaultValue={selectedVehicle.model}
          />
        </label>
        <label>
          Year:
          <input type="text" name="year" defaultValue={selectedVehicle.year} />
        </label>
        <label>
          License plate:
          <input
            type="text"
            name="license_plate"
            defaultValue={selectedVehicle.license_plate}
          />
        </label>
        <label>
          Owner:
          <input
            type="text"
            name="owner"
            defaultValue={selectedVehicle.owner}
          />
        </label>
      </fieldset>
      <div className="button-group">
        <button
          className="btn btn-green"
          onClick={(e) => {
            e.preventDefault();
            handleEditClick(setSelectedVehicle);
          }}
        >
          Edit
        </button>
        <button
          className="btn btn-red"
          onClick={(e) => {
            e.preventDefault();
            handleDeleteClick(setSelectedVehicle);
          }}
        >
          Delete
        </button>
      </div>
    </form>
  );
};
export default ViewVehicleModal;
