import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const VehicleContext = createContext();

export const VehicleProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [vehicles, setVehicles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // SweetAlert2 config
  const Toast = Swal.mixin({
    toast: true,
    position: "top-end",
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.onmouseenter = Swal.stopTimer;
      toast.onmouseleave = Swal.resumeTimer;
    },
  });

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all vehicles
  const fetchVehicles = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("vehicles/");
      setVehicles(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new vehicle
  const createVehicle = async (vehicleData) => {
    try {
      // Debug
      console.log("Sending car data:", vehicleData);

      const response = await axiosInstance.post("vehicles/", vehicleData);
      setVehicles((prevVehicles) => [...prevVehicles, response.data]);
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Vehicle created successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a vehicle
  const updateVehicle = async (vehicleId, updatedFields) => {
    // Debug
    console.log("Updating vehicle data:", updatedFields);

    try {
      const response = await axiosInstance.patch(
        `vehicles/${vehicleId}/`,
        updatedFields
      );
      setVehicles((prevVehicles) =>
        prevVehicles.map((vehicle) =>
          vehicle.id === vehicleId ? response.data : vehicle
        )
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Vehicle updated successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a vehicle
  const deleteVehicle = async (vehicleId) => {
    // Debug
    console.log("Deleting vehicle data:", vehicleId);

    try {
      await axiosInstance.delete(`vehicles/${vehicleId}/`);
      setVehicles((prevVehicles) =>
        prevVehicles.filter((vehicle) => vehicle.id !== vehicleId)
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Vehicle deleted successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Load vehicles when user logs in or changes
  useEffect(() => {
    if (user) fetchVehicles();
    else setVehicles([]);
  }, [user]);

  return (
    <CarContext.Provider
      value={{
        vehicles,
        createVehicle,
        updateVehicle,
        deleteVehicle,
        loading,
        error,
      }}
    >
      {children}
    </CarContext.Provider>
  );
};

// Custom hook for accessing the VehicleContext
export const useVehicleContext = () => useContext(VehicleContext);
