import React, { useState } from "react";
import Swal from "sweetalert2";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Reports.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useUserContext } from "../contexts/UserContext";
import { useReportContext } from "../contexts/ReportContext";
import { useClientContext } from "../contexts/ClientContext";

const Reports = () => {
  const { user } = useAuth();
  const { getUserNameById } = useUserContext();
  const {
    reports,
    selectedReport,
    setSelectedReport,
    updateReport,
    deleteReport,
    createReport,
  } = useReportContext();
  const { clients } = useClientContext();

  const [filters, setFilters] = useState({
    title: "",
    created_date: "",
    client: "",
  });
  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredReports = reports.filter((report) => {
    return (
      (filters.title === "" ||
        report.title.toLowerCase().includes(filters.title.toLowerCase())) &&
      (filters.created_date === "" ||
        report.created_date.includes(filters.created_date)) &&
      (filters.client === "" || report.client.toString() === filters.client)
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(true);
  };
  const handleEditClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newReport) => {
    console.log("user: ", user);
    createReport(newReport);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedReport) => {
    updateReport(selectedReport.id, updatedReport);
    setShowEditModal(false);
    setSelectedReport(null);
  };
  const handleDeleteConfirm = () => {
    deleteReport(selectedReport.id);
    setShowDeleteModal(false);
    setSelectedReport(null);
  };

  // Function to get the client name by ID
  const getClientNameById = (clientId) => {
    const client = clients.find((client) => client.id === clientId);
    return client ? client.name : "Unknown";
  };

  // Get the current date in the format YYYY-MM-DD
  const getCurrentDate = () => {
    const today = new Date();
    return today.toISOString().split("T")[0];
  };

  return (
    <div className="reports-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "title",
            label: "Title",
            value: filters.title,
            placeholder: "Filter by title",
          },
          {
            name: "created_date",
            label: "Created Date",
            value: filters.created_date,
            type: "select",
          },
          {
            name: "client",
            label: "Client",
            value: filters.client,
            type: "select",
          },
          {
            name: "created_by",
            label: "Created By",
            value: filters.created_by,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Reports */}
      <div className="reports-list">
        {filteredReports.map((report) => (
          <div key={report.id} className="report-card">
            <div className="report-infos">
              <h3>{report.title}</h3>
              <p>
                <strong>Created Date:</strong>&nbsp;{report.created_date}
              </p>
              <p>
                <strong>Client:</strong>&nbsp;{getClientNameById(report.client)}
              </p>
              <p>
                <strong>Created By:</strong>&nbsp;
                {getUserNameById(report.created_by)}
              </p>
            </div>
            <div className="report-actions">
              <button
                className="btn btn-blue"
                onClick={() => handleViewClick(report)}
              >
                View
              </button>
              {user.id === report.created_by && (
                <>
                  <button
                    className="btn btn-green"
                    onClick={() => handleEditClick(report)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-red"
                    onClick={() => handleDeleteClick(report)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
        {filteredReports.length === 0 && <p>No reports match your filters.</p>}
      </div>

      {/* Floating New Report Button */}
      <button className="btn-float" onClick={handleCreateClick}>
        New Report
      </button>

      {/* Create Report Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new report */}
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const newReport = {
                title: e.target.title.value,
                description: e.target.description.value,
                created_date: e.target.created_date.value,
                created_by: user.id,
                client: e.target.client.value,
              };
              handleCreateSubmit(newReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  placeholder="Enter report title"
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  placeholder="Enter report description"
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={getCurrentDate()}
                />
              </label>
              <label>
                Client:
                <select name="client">
                  <option value="">Select Client</option>
                  {clients.map((client) => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Report Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Client:
                <select name="client" defaultValue={selectedReport.client}>
                  {clients.map((client) => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            {user.id === selectedReport.created_by && (
              <div className="form-actions">
                <button
                  className="btn btn-green"
                  onClick={(e) => {
                    e.preventDefault();
                    handleEditClick(selectedReport);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-red"
                  onClick={(e) => {
                    e.preventDefault();
                    handleDeleteClick(selectedReport);
                  }}
                >
                  Delete
                </button>
                <button
                  className="btn btn-blue"
                  type="button"
                  onClick={() => setShowViewModal(false)}
                >
                  Close
                </button>
              </div>
            )}
          </form>
        </Modal>
      )}

      {/* Edit Report Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();

              const title = e.target.title.value.trim();
              const description = e.target.description.value.trim();
              const createdDate = e.target.created_date.value;
              const client = e.target.client.value;

              // Validation
              if (title.length < 3) {
                Swal.fire(
                  "Error",
                  "Title must be at least 3 characters long.",
                  "error"
                );
                return;
              }
              if (description.length < 10) {
                Swal.fire(
                  "Error",
                  "Description must be at least 10 characters long.",
                  "error"
                );
                return;
              }
              if (!createdDate) {
                Swal.fire("Error", "Please select a creation date.", "error");
                return;
              }
              if (!client) {
                Swal.fire("Error", "Please select a valid client.", "error");
                return;
              }

              const updatedReport = {
                title: title,
                description: description,
                created_date: createdDate,
                client: client,
              };
              handleEditSubmit(updatedReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Client:
                <select name="client" defaultValue={selectedReport.client}>
                  {clients.map((client) => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowEditModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Report Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this report?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Reports;
