import { useEffect } from "react";

// Contexts
import { useInvoiceContext } from "../contexts/InvoiceContext";
import { useReportContext } from "../contexts/ReportContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useVehicleContext } from "../contexts/VehicleContext";
// Components
import ReportCard from "../components/reports/ReportCard";
// Styles
import "../styles/Invoices.css";

const Invoices = () => {
  const { invoices, fetchInvoices } = useInvoiceContext();
  const { reports, fetchReports } = useReportContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();
  const { getVehicleInfoByVehicleId } = useVehicleContext();

  // Select only the non exported reports
  const exportedReports = reports.filter(
    (report) => report.status === "exported"
  );

  useEffect(() => {
    fetchReports({}, "vehicle__brand, vehicle__model");
    fetchInvoices({}, "issued_date");
  }, []);

  return (
    <>
      <div className="list">
        <h2>Completed reports</h2>
        {exportedReports.length > 0 ? (
          exportedReports.map((item, index) => (
            <section key={index} className="invoices">
              <h3>Report {item.id}</h3>
              <p>Vehicle: {getVehicleInfoByVehicleId(item.id)}</p>
              <p>Owner: {getOwnerNameByVehicleId(item.id)}</p>
              <button>Generate invoice and PDF file</button>
            </section>
          ))
        ) : (
          <p>No completed reports to export.</p>
        )}
      </div>
      <div className="list">
        <h2>Invoices</h2>
        {invoices.length > 0 ? (
          invoices.map((invoice) => (
            <section key={invoice.id} className="invoices">
              <h3>{invoice.invoice_number}</h3>
              <p>Issued: {invoice.formatted_issued_date}</p>
              <p>Total Cost: {invoice.total_cost} CHF</p>
              <button>Download Invoice PDF</button>
            </section>
          ))
        ) : (
          <p>No invoices available.</p>
        )}
      </div>
    </>
  );
};

export default Invoices;
