import React, { useState, useEffect } from "react";
import { Toast } from "../utils/sweetalert";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Reports.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useUserContext } from "../contexts/UserContext";
import { useReportContext } from "../contexts/ReportContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useVehicleContext } from "../contexts/VehicleContext";

const Reports = () => {
  const { user } = useAuth();
  const { getUserNameById } = useUserContext();
  const {
    reports,
    selectedReport,
    setSelectedReport,
    updateReport,
    deleteReport,
    createReport,
  } = useReportContext();
  const { owners } = useOwnerContext();
  const { vehicles } = useVehicleContext();
  const [filters, setFilters] = useState({
    vehicle_id: "",
    user_id: "",
    created_at: "",
    owner: "",
    status: "",
  });
  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredReports = reports.filter((report) => {
    // Find the vehicle associated with this report
    const vehicle = vehicles.find((v) => v.id === report.vehicle_id);
    // Get the owner ID from the vehicle (if it exists)
    const ownerId = vehicle ? vehicle.owner_id : null;

    return (
      (filters.vehicle_id === "" ||
        report.vehicle_id
          .toLowerCase()
          .includes(filters.vehicle_id.toLowerCase())) &&
      (filters.user_id === "" ||
        report.user_id.toString() === filters.user_id) &&
      (filters.created_at === "" ||
        report.created_at.includes(filters.created_at)) &&
      (filters.owner === "" || ownerId?.toString() === filters.owner) &&
      (filters.status === "" || report.status.toString() === filters.status)
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(true);
  };
  const handleEditClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (report) => {
    setSelectedReport(report);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newReport) => {
    console.log("user: ", user);
    createReport(newReport);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedReport) => {
    updateReport(selectedReport.id, updatedReport);
    setShowEditModal(false);
    setSelectedReport(null);
  };
  const handleDeleteConfirm = () => {
    deleteReport(selectedReport.id);
    setShowDeleteModal(false);
    setSelectedReport(null);
  };

  // Function to get the owner name by vehicle ID
  const getOwnerNameByVehicleId = (vehicleId) => {
    const vehicle = vehicles.find((vehicle) => vehicle.id === vehicleId);
    const owner = owners.find((owner) => owner.id === vehicle.owner_id);
    return owner ? owner.name : "Unknown";
  };

  // Function to get the license_plate by vehicle ID
  const getVehicleInfoByVehicleId = (vehicleId) => {
    const vehicle = vehicles.find((vehicle) => vehicle.id === vehicleId);
    return vehicle ? vehicle.__str__ : "Unknown";
  };

  // Get the current date in the format YYYY-MM-DD
  const getCurrentDate = () => {
    const today = new Date();
    return today.toISOString().split("T")[0];
  };

  return (
    <div className="reports-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "vehicle",
            label: "Vehicle",
            value: filters.vehicle_id,
            type: "select",
          },
          {
            name: "created_date",
            label: "Created Date",
            value: filters.created_date,
            type: "select",
          },
          {
            name: "owner",
            label: "Owner",
            value: filters.owner,
            type: "select",
          },
          {
            name: "created_by",
            label: "Created By",
            value: filters.created_by,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Reports */}
      <div className="reports-list">
        {filteredReports.map((report) => (
          <div key={report.id} className="report-card">
            <div className="report-infos">
              <h3>{getVehicleInfoByVehicleId(report.vehicle_id)}</h3>
              <p>
                <strong>Created Date:</strong>&nbsp;{report.created_date}
              </p>
              <p>
                <strong>Owner:</strong>&nbsp;
                {getOwnerNameByVehicleId(report.vehicle_id)}
              </p>
              <p>
                <strong>Status:</strong>&nbsp;{report.status}
              </p>
              <p>
                <strong>Created By:</strong>&nbsp;
                {getUserNameById(report.created_by)}
              </p>
            </div>
            <div className="report-actions">
              <button
                className="btn btn-blue"
                onClick={() => handleViewClick(report)}
              >
                View
              </button>
              {user.id === report.created_by && (
                <>
                  <button
                    className="btn btn-green"
                    onClick={() => handleEditClick(report)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-red"
                    onClick={() => handleDeleteClick(report)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
        {filteredReports.length === 0 && <p>No reports match your filters.</p>}
      </div>

      {/* Floating New Report Button */}
      <button className="btn-float" onClick={handleCreateClick}>
        New Report
      </button>

      {/* Create Report Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new report */}
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const newReport = {
                title: e.target.title.value,
                description: e.target.description.value,
                created_date: e.target.created_date.value,
                created_by: user.id,
                client: e.target.client.value,
              };
              handleCreateSubmit(newReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  placeholder="Enter report title"
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  placeholder="Enter report description"
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={getCurrentDate()}
                />
              </label>
              <label>
                Owner:
                <select name="owner">
                  <option value="">Select Owner</option>
                  {owners.map((owner) => (
                    <option key={owner.id} value={owner.id}>
                      {owner.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Report Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Client:
                <select name="owner" defaultValue={selectedReport.owner}>
                  {owners.map((owner) => (
                    <option key={owner.id} value={owner.id}>
                      {owner.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            {user.id === selectedReport.created_by && (
              <div className="form-actions">
                <button
                  className="btn btn-green"
                  onClick={(e) => {
                    e.preventDefault();
                    handleEditClick(selectedReport);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-red"
                  onClick={(e) => {
                    e.preventDefault();
                    handleDeleteClick(selectedReport);
                  }}
                >
                  Delete
                </button>
                <button
                  className="btn btn-blue"
                  type="button"
                  onClick={() => setShowViewModal(false)}
                >
                  Close
                </button>
              </div>
            )}
          </form>
        </Modal>
      )}

      {/* Edit Report Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();

              const title = e.target.title.value.trim();
              const description = e.target.description.value.trim();
              const createdDate = e.target.created_date.value;
              const owner = e.target.owner.value;

              // Validation
              if (title.length < 3) {
                Toast.fire(
                  "Error",
                  "Title must be at least 3 characters long.",
                  "error"
                );
                return;
              }
              if (description.length < 10) {
                Toast.fire(
                  "Error",
                  "Description must be at least 10 characters long.",
                  "error"
                );
                return;
              }
              if (!createdDate) {
                Toast.fire("Error", "Please select a creation date.", "error");
                return;
              }
              if (!owner) {
                Toast.fire("Error", "Please select a valid owner.", "error");
                return;
              }

              const updatedReport = {
                title: title,
                description: description,
                created_date: createdDate,
                owner: owner,
              };
              handleEditSubmit(updatedReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Owner:
                <select name="owner" defaultValue={selectedReport.owner}>
                  {owners.map((owner) => (
                    <option key={owner.id} value={owner.id}>
                      {owner.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowEditModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Report Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this report?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Reports;
