                <path d="M15 11.25C15.4142 11.25 15.75 11.5858 15.75 12C15.75 12.4142 15.4142 12.75 15 12.75H4.02744L5.98809 14.4306C6.30259 14.7001 6.33901 15.1736 6.06944 15.4881C5.79988 15.8026 5.3264 15.839 5.01191 15.5694L1.51191 12.5694C1.34567 12.427 1.25 12.2189 1.25 12C1.25 11.7811 1.34567 11.573 1.51191 11.4306L5.01191 8.43056C5.3264 8.16099 5.79988 8.19741 6.06944 8.51191C6.33901 8.8264 6.30259 9.29988 5.98809 9.56944L4.02744 11.25H15Z" />
              </svg>
            </button>
          </div>
          <ul>
            <li>
              <Link
                to="/dashboard"
                className={location.pathname === "/dashboard" ? "active" : ""}
                disabled={location.pathname === "/dashboard"}
                aria-disabled={location.pathname === "/dashboard"}
              >
                Dashboard
              </Link>
            </li>
            <li>
              <Link
                to="/report"
                className={location.pathname === "/report" ? "active" : ""}
                disabled={location.pathname === "/report"}
                aria-disabled={location.pathname === "/report"}
              >
                Reports
              </Link>
            </li>
            <li>
              <Link
                to="/owner"
                className={location.pathname === "/owner" ? "active" : ""}
                disabled={location.pathname === "/owner"}
                aria-disabled={location.pathname === "/owner"}
              >
                Owners
              </Link>
            </li>
            <li>
              <Link
                to="/vehicle"
                className={location.pathname === "/vehicle" ? "active" : ""}
                disabled={location.pathname === "/vehicle"}
                aria-disabled={location.pathname === "/vehicle"}
              >
                Vehicles
              </Link>
            </li>
            <li>
              <Link
                to="/invoices"
                className={location.pathname === "/invoices" ? "active" : ""}
                disabled={location.pathname === "/invoices"}
                aria-disabled={location.pathname === "/invoices"}
              >
                Invoices
              </Link>
            </li>
            <li>
              <Link
                to="/inventory"
                className={location.pathname === "/inventory" ? "active" : ""}
                disabled={location.pathname === "/inventory"}
                aria-disabled={location.pathname === "/inventory"}
              >
                Inventory
              </Link>
            </li>
            <li>
              <Link
                to="/tasktemplate"
                className={
                  location.pathname === "/tasktemplate" ? "active" : ""
                }
                disabled={location.pathname === "/tasktemplate"}
                aria-disabled={location.pathname === "/tasktemplate"}
              >
                Task templates
              </Link>
            </li>
          </ul>
          {/* Floating create Button */}
          <CreateItemButton />
        </>
      )}
    </nav>
  );
}

export default Sidebar;
