// src/context/ReportContext.js
import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

const BASE_URL = "http://localhost:8000";

export const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);

  // Fetch reports from the server
  const fetchReports = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/reports/`);
      setReports(response.data);
    } catch (error) {
      console.error("Error fetching reports:", error);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      const response = await axios.post(`${BASE_URL}/api/reports/`, reportData);
      setReports([...reports, response.data]);
    } catch (error) {
      console.error("Error creating report:", error);
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedData) => {
    try {
      const response = await axios.put(
        `${BASE_URL}/api/reports/${reportId}/`,
        updatedData
      );
      setReports(reports.map((r) => (r.id === reportId ? response.data : r)));
    } catch (error) {
      console.error("Error updating report:", error);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    try {
      await axios.delete(`${BASE_URL}/api/reports/${reportId}/`);
      setReports(reports.filter((r) => r.id !== reportId));
    } catch (error) {
      console.error("Error deleting report:", error);
    }
  };

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        fetchReports,
        createReport,
        updateReport,
        deleteReport,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};
