// Contexts
import { useUserContext } from "../contexts/UserContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useVehicleContext } from "../contexts/VehicleContext";

const OwnerCard = ({
  item,
  handleViewClick,
  handleEditClick,
  handleDeleteClick,
}) => {
  const { getUserNameById } = useUserContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();
  const { getVehicleInfoByVehicleId } = useVehicleContext();

  // Return a truncated text
  const truncateText = (text, maxLength = 25) => {
    return text.length > maxLength ? text.slice(0, maxLength) + "..." : text;
  };

  return (
    <div
      key={owner.id}
      className="card"
      title="View Owner"
      onClick={(e) => {
        // Prevent triggering view mode if clicking on an action button
        if (!e.target.closest(".actions")) {
          handleViewClick(owner);
        }
      }}
    >
      <div>
        <h3>{owner.full_name}</h3>
        <p>
          <strong>Email:</strong>&nbsp;{owner.email}
        </p>
        <p>
          <strong>Phone:</strong>&nbsp;{owner.phone}
        </p>
        <p>
          <strong>Address:</strong>&nbsp;
          {owner.address}
        </p>
      </div>
      <div className="actions">
        <button
          className="btn btn-green"
          onClick={() => handleEditClick(owner)}
        >
          Edit
        </button>
        <button
          className="btn btn-red"
          onClick={() => handleDeleteClick(owner)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};
export default OwnerCard;
