import { useState } from "react";
import "../styles/CreateItemButton.css"; // Create a CSS file for styling

const CreateItemButton = ({ itemType, handleCreateClick }) => {
  const [visible, setVisible] = useState(true);

  return (
    <div className={`create-item-button-container${visible ? " show" : ""}`}>
      <button
        title={`Create new ${itemType}`}
        type="button"
        className="create-item-button"
        onClick={handleCreateClick}
      >
        <svg
          className="icon"
          viewBox="0 0 21 21"
          xmlns="http://www.w3.org/2000/svg"
          fill="currentColor"
        >
          <path d="M10.5 0C4.977 0 0 4.477 0 10.5S4.977 21 10.5 21 21 16.523 21 10.5 16.023 0 10.5 0zm0 19c-4.687 0-8.5-3.813-8.5-8.5S5.813 2 10.5 2s8.5 3.813 8.5 8.5-3.813 8.5-8.5 8.5zm1-9h3a1 1 0 1 1 0 2h-3v3a1 1 0 1 1-2 0v-3H6a1 1 0 1 1 0-2h3V7a1 1 0 1 1 2 0v3z" />
        </svg>
      </button>
    </div>
  );
};

export default CreateItemButton;
