import React, { useState } from "react";
// Components
import FilterBar from "../components/FilterBar";
import CreateOrEditVehicleModal from "../components/CreateOrEditVehicleModal";
import ViewVehicleModal from "../components/ViewVehicleModal";
import Modal from "../components/Modal";
// Styles
import "../styles/Vehicles.css";
import "../styles/Cards.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";

const Vehicles = () => {
  const { authenticatedUser } = useAuth();
  const {
    vehicles,
    selectedVehicle,
    setSelectedVehicle,
    createVehicle,
    updateVehicle,
    deleteVehicle,
    getVehicleInfoByVehicleId,
  } = useVehicleContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();

  const [filters, setFilters] = useState({
    brand: "",
    model: "",
    year: "",
    license_plate: "",
    vehicle_owner: "",
  });

  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredVehicles = vehicles.filter((vehicle) => {
    return (
      (filters.brand === "" ||
        vehicle.brand.toLowerCase().includes(filters.brand.toLowerCase())) &&
      (filters.model === "" ||
        vehicle.model.toLowerCase().includes(filters.model.toLowerCase())) &&
      (filters.year === "" || vehicle.year.toString().includes(filters.year)) &&
      (filters.license_plate === "" ||
        vehicle.license_plate
          .toLowerCase()
          .includes(filters.license_plate.toLowerCase())) &&
      (filters.vehicle_owner === "" ||
        (vehicle.owner &&
          vehicle.owner.toString().includes(filters.vehicle_owner.toString())))
    );
  });

  const handleCreateClick = () => {
    setSelectedVehicle(null);
    setShowDeleteModal(false);
    setShowViewModal(false);
    setShowCreateModal(true);
  };
  const handleViewClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowCreateModal(false);
    setShowDeleteModal(false);
    setShowViewModal(true);
  };
  const handleEditClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowDeleteModal(false);
    setShowCreateModal(true);
  };
  const handleDeleteClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowCreateModal(false);
    setShowDeleteModal(true);
  };
  const handleDeleteConfirm = () => {
    deleteVehicle(selectedVehicle.id);
    setShowDeleteModal(false);
    setSelectedVehicle(null);
  };

  return (
    <div className="vehicles-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "brand",
            label: "Brand",
            value: filters.brand,
            type: "select",
          },
          {
            name: "model",
            label: "Model",
            value: filters.model,
            type: "select",
          },
          {
            name: "year",
            label: "Year",
            value: filters.year,
            type: "select",
          },
          {
            name: "license_plate",
            label: "License plate",
            value: filters.license_plate,
            placeholder: "Filter by License plate",
          },
          {
            name: "vehicle_owner",
            label: "Owner",
            value: filters.owner,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Vehicles */}
      <div className="list">
        {filteredVehicles
          .sort((a, b) => a.__str__.localeCompare(b.__str__))
          .map((vehicle) => (
            <div
              key={vehicle.id}
              className="card"
              title="View Vehicle"
              onClick={(e) => {
                // Prevent triggering view mode if clicking on an action button
                if (!e.target.closest(".actions")) {
                  handleViewClick(vehicle);
                }
              }}
            >
              <div>
                <h3>{getVehicleInfoByVehicleId(vehicle.id)}</h3>
                <p>
                  <strong>Year:</strong>&nbsp;{vehicle.year}
                </p>
                <p>
                  <strong>Owner:</strong>&nbsp;
                  {getOwnerNameByVehicleId(vehicle.id)}
                </p>
              </div>
              <div className="actions">
                <button
                  className="btn btn-green"
                  onClick={() => handleEditClick(vehicle)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-red"
                  onClick={() => handleDeleteClick(vehicle)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        {filteredVehicles.length === 0 && (
          <p>No vehicles match your filters.</p>
        )}
      </div>

      {/* Floating New Vehicle Button */}
      <button className="btn btn-float" onClick={handleCreateClick}>
        New Vehicle
      </button>

      {/* Create or Edit Vehicle Modal */}
      {showCreateModal && (
        <CreateOrEditVehicleModal
          onClose={() => setShowCreateModal(false)}
          onDelete={() => handleDeleteClick(selectedVehicle)}
        />
      )}

      {/* View Vehicle Modal */}
      {showViewModal && (
        <ViewVehicleModal
          onClose={() => setShowViewModal(false)}
          onEdit={() => handleEditClick(selectedVehicle)}
          onDelete={() => handleDeleteClick(selectedVehicle)}
        />
      )}

      {/* Delete Vehicle Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this vehicle?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Vehicles;
