import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const CarContext = createContext();

export const CarProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // SweetAlert2 config
  const Toast = Swal.mixin({
    toast: true,
    position: "top-end",
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.onmouseenter = Swal.stopTimer;
      toast.onmouseleave = Swal.resumeTimer;
    },
  });

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all cars
  const fetchCars = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("cars/");
      setCars(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createCar = async (carData) => {
    try {
      // Debug
      console.log("Sending car data:", carData);

      const response = await axiosInstance.post("cars/", carData);
      setReports((prevCars) => [...prevCars, response.data]);
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Car created successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a car
  const updateCar = async (carId, updatedFields) => {
    // Debug
    console.log("Updating car data:", updatedFields);

    try {
      const response = await axiosInstance.patch(
        `cars/${carId}/`,
        updatedFields
      );
      setReports((prevCars) =>
        prevCars.map((car) => (car.id === carId ? response.data : car))
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Car updated successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a car
  const deleteCar = async (carId) => {
    // Debug
    console.log("Deleting car data:", carId);

    try {
      await axiosInstance.delete(`cars/${carId}/`);
      setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Car deleted successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Load cars when user logs in or changes
  useEffect(() => {
    if (user) fetchCars();
    else setCars([]);
  }, [user]);

  return (
    <CarContext.Provider
      value={{
        cars,
        createCar,
        updateCar,
        deleteCar,
        loading,
        error,
      }}
    >
      {children}
    </CarContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useCarContext = () => useContext(CarContext);
