import { useState } from "react";
// Utils
import getFilterOptions from "../utils/filterBarFilterOptions";
// Components
import Page from "../components/Page";
import ReportCard from "../components/ReportCard";
import ReportModal from "../components/ReportModal";
// Contexts
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
// Styles
import "../styles/Report.css";

const Report = () => {
  const { reports, deleteReportWithAlert } = useReportContext();
  const { getVehicleInfoByVehicleId } = useVehicleContext();
  const [filters, setFilters] = useState({
    vehicle: "",
    user: "",
    created_at: "",
    owner: "",
    status: "",
  });

  return (
    <Page
      itemType="Report"
      filters={{ ...filters, type: "report" }}
      setFilters={setFilters}
      filterOptions={getFilterOptions(filters).reports}
      sortingCardFunction={(a, b) => {
        // Sort by status
        const statusComparison = b.status.localeCompare(a.status);
        if (statusComparison !== 0) return statusComparison;

        // If status is the same, sort by vehicle info
        return getVehicleInfoByVehicleId(a.id).localeCompare(
          getVehicleInfoByVehicleId(b.id)
        );
      }}
      items={reports}
      deleteItemWithAlert={deleteReportWithAlert}
      CardComponent={ReportCard}
      ModalComponent={ReportModal}
    />
  );
};

export default Report;
