import { Link, useLocation } from "react-router-dom";
import "../styles/Sidebar.css";
import { useAuth } from "../contexts/AuthContext";

function Sidebar() {
  const { authenticatedUser, logout } = useAuth();
  const location = useLocation();

  const capitalizeFirstLetter = (string) => {
    return string?.charAt(0).toUpperCase() + string?.toLowerCase().slice(1);
  };

  return (
    <nav className="sidebar">
      {authenticatedUser && (
        <>
          <div>
            <img src="images/logo-garage.svg" alt="logo" />
            <p className="sidebar-welcome">
              Welcome
              <br />
              {capitalizeFirstLetter(authenticatedUser.username)}
            </p>
            <button className="btn btn-blue logout" onClick={logout}>
              LOG OUT
            </button>
          </div>
          <ul>
            <li>
              <Link
                to="/dashboard"
                className={location.pathname === "/dashboard" ? "active" : ""}
              >
                Dashboard
              </Link>
            </li>
            <li>
              <Link
                to="/reports"
                className={location.pathname === "/reports" ? "active" : ""}
              >
                Reports
              </Link>
            </li>
            <li>
              <Link
                to="/clients"
                className={location.pathname === "/clients" ? "active" : ""}
              >
                Owners
              </Link>
            </li>
            <li>
              <Link
                to="/vehicles"
                className={location.pathname === "/vehicles" ? "active" : ""}
              >
                Vehicles
              </Link>
            </li>
            <li>
              <Link
                to="/bills"
                className={location.pathname === "/bills" ? "active" : ""}
              >
                Bills
              </Link>
            </li>
          </ul>
        </>
      )}
    </nav>
  );
}

export default Sidebar;
