import axios from "axios";

// Base instance for axios
const authAxios = axios.create({
  baseURL: "http://localhost:8000", // Set the base URL for your API
});

// Request Interceptor
authAxios.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("access_token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    // Handle request errors here
    return Promise.reject(error);
  }
);

// Response Interceptor
authAxios.interceptors.response.use(
  (response) => response, // Pass successful responses
  async (error) => {
    const originalRequest = error.config;

    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true; // Prevent infinite retries

      try {
        // Attempt to refresh the token
        await refreshToken();
        const newToken = localStorage.getItem("access_token");

        if (newToken) {
          originalRequest.headers.Authorization = `Bearer ${newToken}`;
          return authAxios(originalRequest); // Retry the failed request
        }
      } catch (err) {
        // If token refresh fails, log out the user
        logout();
      }
    }

    return Promise.reject(error); // Reject the promise if the error is not recoverable
  }
);

export default authAxios;
