import React from "react";
import Modal from "../components/Modal";
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";

const ViewReportModal = ({ onClose }) => {
  const { selectedReport, getRepairsByReport } = useReportContext();
  const { getVehicleInfoByVehicleId } = useVehicleContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();

  return (
    <Modal onClose={onClose}>
      <div className="auth-container">
        <div className="auth-card">
          <h2>Report Details</h2>

          <div className="report-details">
            <p>
              <strong>Vehicle:</strong>{" "}
              {getVehicleInfoByVehicleId(selectedReport.vehicle_id)}
            </p>
            <p>
              <strong>Owner:</strong>{" "}
              {getOwnerNameByVehicleId(selectedReport.vehicle_id)}
            </p>
            <p>
              <strong>Status:</strong> {selectedReport.status}
            </p>
            <p>
              <strong>Remarks:</strong> {selectedReport.remarks || "No remarks"}
            </p>
            <p>
              <strong>Created At:</strong> {selectedReport.formatted_created_at}
            </p>
          </div>

          <h3>Repairs</h3>
          {getRepairsByReport(selectedReport.id).length > 0 ? (
            <ul className="repair-list">
              {selectedReport.repairs.map((repair, index) => (
                <li key={index}>
                  {repair.description} - â‚¬{repair.total_cost}
                </li>
              ))}
            </ul>
          ) : (
            <p>No repairs recorded.</p>
          )}

          <div className="button-group">
            <button type="button" className="btn cancel-btn" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export default ViewReportModal;
