// Contexts
import { useInvoiceContext } from "../contexts/InvoiceContext";
import { useReportContext } from "../contexts/ReportContext";
// Styles
import "../styles/Invoices.css";

const Invoices = () => {
  const { invoices, downloadInvoicePDF } = useInvoiceContext();
  const { reports, fetchReports } = useReportContext();

  useEffect(() => {
    fetchReports({ status: "completed" }, "vehicle__brand, vehicle__model");
  }, []);

  return (
    <>
      <div className="list">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <CardComponent
              key={item.id}
              item={item}
              handleViewClick={handleViewClick}
              handleEditClick={handleEditClick}
              {...(itemType === "Report" ? { handleExportClick } : {})}
              handleDeleteClick={handleDeleteClick}
            />
          ))
        ) : (
          <p>No {itemType} match your filters.</p>
        )}
      </div>
      <section className="invoices">
        <h2>Invoices</h2>
        <button onClick={() => downloadInvoicePDF(invoiceId)}>
          Download Invoice PDF
        </button>
      </section>
    </>
  );
};

export default Invoices;
