import React, { createContext, useState, useEffect } from "react";
import axios from "axios";
import AuthContext from "./AuthContext";

const BASE_URL = "http://localhost:8000";

export const ClientContext = createContext();

export const ClientProvider = ({ children }) => {
  const { user } = useContext(AuthContext);
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Helper function to get headers with token
  const getHeaders = () => {
    const token = localStorage.getItem("access_token");
    if (!token) {
      throw new Error("User is not authenticated");
    }
    return {
      Authorization: `Bearer ${token}`,
    };
  };

  // Fetch clients from the server
  const fetchClients = async () => {
    if (!user) return;

    try {
      setLoading(true);
      const response = await axios.get(`${BASE_URL}/api/clients/`, {
        headers: getHeaders(),
      });
      setClients(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new client
  const createClient = async (newClient) => {
    if (!user) throw new Error("User is not authenticated");

    try {
      const response = await axios.post(`${BASE_URL}/api/clients/`, newClient, {
        headers: getHeaders(),
      });
      setClients([...clients, response.data]);
    } catch (err) {
      handleError(err);
    }
  };

  // Update a client
  const updateClient = async (id, updatedClient) => {
    if (!user) throw new Error("User is not authenticated");

    try {
      const response = await axios.put(
        `${BASE_URL}/api/clients/${id}/`,
        updatedClient,
        { headers: getHeaders() }
      );
      setClients(
        clients.map((client) =>
          client.id === id ? { ...client, ...response.data } : client
        )
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a client
  const deleteClient = async (id) => {
    if (!user) throw new Error("User is not authenticated");

    try {
      await axios.delete(`${BASE_URL}/api/clients/${id}/`, {
        headers: getHeaders(),
      });
      setClients(clients.filter((client) => client.id !== id));
    } catch (err) {
      handleError(err);
    }
  };

  // Fetch clients when the provider mounts or when the user changes
  useEffect(() => {
    if (user) {
      fetchClients();
    } else {
      setClients([]);
    }
  }, [user]);

  return (
    <ClientContext.Provider
      value={{
        clients,
        selectedClient,
        setSelectedClient,
        fetchClients,
        createClient,
        updateClient,
        deleteClient,
        loading,
        error,
      }}
    >
      {children}
    </ClientContext.Provider>
  );
};
