import { useState } from "react";
// Components
import FilterBar from "./FilterBar";
// Styles
import "../styles/Cards.css";
// Utils
import { filterItems } from "../utils/pageItemFilter";

const Page = ({
  title,
  filters,
  setFilters,
  filterOptions,
  items,
  deleteItemWithAlert,
  CardComponent,
  ModalComponent,
}) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  // Filter items based on filters
  const filteredItems = items.filter((item) => filterItems(item, filters));

  const handleCreateClick = () => {
    setSelectedItem(null);
    setShowDeleteModal(false);
    setShowViewModal(false);
    setShowCreateModal(true);
  };

  const handleViewClick = (item) => {
    setSelectedItem(item);
    setShowCreateModal(false);
    setShowDeleteModal(false);
    setShowViewModal(true);
  };

  const handleEditClick = (item) => {
    setSelectedItem(item);
    setShowViewModal(false);
    setShowDeleteModal(false);
    setShowCreateModal(true);
  };

  const handleDeleteClick = (item) => {
    setSelectedItem(item);
    setShowViewModal(false);
    setShowCreateModal(false);
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = () => {
    deleteItemWithAlert(selectedItem.id); // Update based on entity
    setShowDeleteModal(false);
    setSelectedItem(null);
  };

  return (
    <>
      {/* Filter bar with filter options */}
      <FilterBar
        filterOptions={filterOptions}
        onFilterChange={handleFilterChange}
      />

      {/* Items list with card display */}
      <div className="list">
        {filteredItems.length > 0 ? (
          items
            .sort((a, b) => b.status.localeCompare(a.status))
            .map((item) => (
              <CardComponent
                key={item.id}
                item={item}
                handleViewClick={handleViewClick}
                handleEditClick={handleEditClick}
                handleDeleteClick={handleDeleteClick}
              />
            ))
        ) : (
          <p>No items match your filters.</p>
        )}
      </div>

      {/* Floating create Button */}
      <button className="btn btn-float" onClick={handleCreateClick}>
        New {title}
      </button>

      {showViewModal && (
        <ModalComponent
          item={selectedItem}
          onClose={() => setShowViewModal(false)}
        />
      )}
      {showCreateModal && (
        <ModalComponent item={null} onClose={() => setShowCreateModal(false)} />
      )}
      {showDeleteModal && (
        <ModalComponent
          item={selectedItem}
          onClose={() => setShowDeleteModal(false)}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </>
  );
};
export default Page;
