import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import { Toast } from "../utils/sweetalert";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all reports
  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("reports/");
      setReports(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      // Debug
      console.log("Sending report data:", reportData);

      const response = await axiosInstance.post("reports/", reportData);
      setReports((prevReports) => [...prevReports, response.data]);
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report created successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedFields) => {
    // Debug
    console.log("Updating report data:", updatedFields);

    try {
      const response = await axiosInstance.patch(
        `reports/${reportId}/`,
        updatedFields
      );
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report updated successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    // Debug
    console.log("Deleting report data:", reportId);

    try {
      await axiosInstance.delete(`reports/${reportId}/`);
      setReports((prevReports) =>
        prevReports.filter((report) => report.id !== reportId)
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report deleted successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Create repairs
  const createRepair = () => {
    console.log("create repairs");
  };

  const getRepairsByReport = async (reportId) => {
    try {
      const response = await useAxios.get(`reports/${reportId}/`);
      return response.data.repairs;
    } catch (error) {
      console.error("Error fetching repairs:", error);
      return [];
    }
  };

  // Load reports when user logs in or changes
  useEffect(() => {
    if (user) fetchReports();
    else setReports([]);
  }, [user]);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        createRepair,
        getRepairsByReport,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
