import React, { createContext, useState, useEffect, useContext } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const OwnerContext = createContext();

export const OwnerProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [owners, setOwners] = useState([]);
  const [selectedOwner, setSelectedOwner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // SweetAlert2 config
  const Toast = Swal.mixin({
    toast: true,
    position: "top-end",
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.onmouseenter = Swal.stopTimer;
      toast.onmouseleave = Swal.resumeTimer;
    },
  });

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all clients
  const fetchOwners = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("owners/");
      setOwners(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new owner
  const createOwner = async (ownerData) => {
    try {
      // Debug
      console.log("Sending owner data:", ownerData);

      const response = await axiosInstance.post("owners/", ownerData);
      setOwners((prevOwners) => [...prevOwners, response.data]);
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Owner created successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a owner
  const updateOwner = async (ownerId, updatedFields) => {
    // Debug
    console.log("Updating owner data:", updatedFields);

    try {
      const response = await axiosInstance.patch(
        `owners/${ownerId}/`,
        updatedFields
      );
      setOwners((prevOwners) =>
        prevOwners.map((owner) =>
          owner.id === ownerId ? response.data : owner
        )
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Owner updated successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a owner
  const deleteOwner = async (ownerId) => {
    // Debug
    console.log("Deleting owner data:", ownerId);

    try {
      await axiosInstance.delete(`owners/${ownerId}/`);
      setOwners((prevOwners) =>
        prevOwners.filter((owner) => owner.id !== ownerId)
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Owner deleted successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Fetch owners when user logs in or changes
  useEffect(() => {
    if (user) fetchOwners();
    else setOwners([]); // Clear owners if no user
  }, [user]);

  return (
    <ClientContext.Provider
      value={{
        owners,
        selectedOwner,
        setSelectedOwner,
        createOwner,
        updateOwner,
        deleteOwner,
        loading,
        error,
      }}
    >
      {children}
    </ClientContext.Provider>
  );
};

// Custom hook for accessing the OwnerContext
export const useOwnerContext = () => useContext(OwnerContext);
