import React, { useEffect, useState } from "react";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Clients.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useClientContext } from "../contexts/ClientContext";

const Clients = () => {
  const { user } = useAuth();
  const {
    clients,
    selectedClient,
    setSelectedClient,
    createClient,
    updateClient,
    deleteClient,
  } = useClientContext();

  const [filters, setFilters] = useState({
    name: "",
    email: "",
  });

  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredClients = clients.filter((client) => {
    return (
      (filters.name === "" ||
        client.name.toLowerCase().includes(filters.name.toLowerCase())) &&
      (filters.email === "" || client.email.includes(filters.email))
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (client) => {
    setSelectedClient(client);
    setShowViewModal(true);
  };
  const handleEditClick = (client) => {
    setSelectedClient(client);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (client) => {
    setSelectedClient(client);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newClient) => {
    createClient(newClient);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedClient) => {
    updateClient(selectedClient.id, updatedClient);
    setShowEditModal(false);
    setSelectedClient(null);
  };
  const handleDeleteConfirm = () => {
    deleteClient(selectedClient.id);
    setShowDeleteModal(false);
    setSelectedClient(null);
  };

  return (
    <div className="clients-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "name",
            label: "Name",
            value: filters.name,
            placeholder: "Filter by name",
          },
          {
            name: "email",
            label: "Email",
            value: filters.email,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Clients */}
      <div className="clients-list">
        {filteredClients.map((client) => (
          <div key={client.id} className="client-card">
            <div className="client-infos">
              <h3>{client.name}</h3>
              <p>
                <strong>Email:</strong>&nbsp;{client.email}
              </p>
              <p>
                <strong>Phone:</strong>&nbsp;{client.phone}
              </p>
              <p>
                <strong>Address:</strong>&nbsp;
                {client.address}
              </p>
            </div>
            <div className="client-actions">
              <button
                className="btn btn-blue"
                onClick={() => handleViewClick(client)}
              >
                View
              </button>
              <button
                className="btn btn-green"
                onClick={() => handleEditClick(client)}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={() => handleDeleteClick(client)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
        {filteredClients.length === 0 && <p>No clients match your filters.</p>}
      </div>

      {/* Floating New Client Button */}
      <button className="btn-float" onClick={handleCreateClick}>
        New Client
      </button>

      {/* Create Client Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new client */}
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const newClient = {
                name: e.target.name.value,
                email: e.target.email.value,
                phone: e.target.phone.value,
                address: e.target.address.value,
              };
              handleCreateSubmit(newClient);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Client</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  placeholder="Enter client name"
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  placeholder="Enter client email address"
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  placeholder="Enter client phone number"
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  placeholder="Enter client address"
                />
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Client Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Client</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  defaultValue={selectedClient.name}
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  defaultValue={selectedClient.email}
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  defaultValue={selectedClient.phone}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  defaultValue={selectedClient.address}
                />
              </label>
            </fieldset>
            {user.id === selectedReport.created_by && (
              <div className="form-actions">
                <button
                  className="btn btn-green"
                  onClick={(e) => {
                    e.preventDefault();
                    handleEditClick(selectedReport);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-red"
                  onClick={(e) => {
                    e.preventDefault();
                    handleDeleteClick(selectedReport);
                  }}
                >
                  Delete
                </button>
                <button
                  className="btn btn-blue"
                  type="button"
                  onClick={() => setShowViewModal(false)}
                >
                  Close
                </button>
              </div>
            )}
          </form>
        </Modal>
      )}

      {/* Edit Report Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const updatedReport = {
                title: e.target.title.value,
                created_date: e.target.created_date.value,
                client: e.target.client.value,
              };
              handleEditSubmit(updatedReport);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Report</legend>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  defaultValue={selectedReport.title}
                />
              </label>
              <label>
                Description:
                <textarea
                  type="text"
                  name="description"
                  defaultValue={selectedReport.description}
                />
              </label>
              <label>
                Created Date:
                <input
                  type="date"
                  name="created_date"
                  defaultValue={
                    selectedReport.created_date
                      ? new Date(selectedReport.created_date)
                          .toISOString()
                          .split("T")[0]
                      : ""
                  }
                />
              </label>
              <label>
                Client:
                <select name="client" defaultValue={selectedReport.client}>
                  {clients.map((client) => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </select>
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowEditModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Report Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this report?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Clients;
