import { useState } from "react";

// Contexts
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/ReportModal.css";

const CreateOrEditVehicleModal = ({ onClose, onDelete }) => {
  const {
    vehicles,
    selectedVehicle,
    setSelectedVehicle,
    createVehicle,
    updateVehicle,
    deleteVehicle,
    getVehicleInfoByVehicleId,
  } = useVehicleContext();

  const handleCreateSubmit = (newVehicle) => {
    createVehicle(newVehicle);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedVehicle) => {
    updateVehicle(selectedVehicle.id, updatedVehicle);
    setShowEditModal(false);
    setSelectedVehicle(null);
  };

  return (
    <div className="modal-container">
      <div className="modal-card">
        <svg
          onClick={onClose}
          className="modal-card-close"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
        >
          <path
            width="100%"
            height="100%"
            d="M11.414 10l2.829-2.828a1 1 0 1 0-1.415-1.415L10 8.586 7.172 5.757a1 1 0 0 0-1.415 1.415L8.586 10l-2.829 2.828a1 1 0 0 0 1.415 1.415L10 11.414l2.828 2.829a1 1 0 0 0 1.415-1.415L11.414 10zM10 20C4.477 20 0 15.523 0 10S4.477 0 10 0s10 4.477 10 10-4.477 10-10 10z"
          />
        </svg>
        {selectedVehicle ? <h2>Edit Vehicle</h2> : <h2>Create Vehicle</h2>}
        <form
          className="modal-form"
          onSubmit={selectedReport ? handleEditSubmit : handleCreateSubmit}
        >
          <fieldset className="form-inputs">
            <legend>Create New Vehicle</legend>
            <label>
              Brand:
              <input
                type="text"
                name="brand"
                placeholder="Enter vehicle brand"
              />
            </label>
            <label>
              Model:
              <input
                type="text"
                name="model"
                placeholder="Enter vehicle model"
              />
            </label>
            <label>
              Year:
              <input type="text" name="year" placeholder="Enter vehicle year" />
            </label>
            <label>
              License plate:
              <input
                type="text"
                name="license_plate"
                placeholder="Enter vehicle license plate"
              />
            </label>
            <label>
              Owner:
              <input
                type="text"
                name="owner"
                placeholder="Enter vehicle owner"
              />
            </label>
          </fieldset>
          <div className="button-group">
            <button className="btn btn-green" type="submit">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
export default CreateOrEditVehicleModal;
