import React, { useEffect } from "react";

// Contexts
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useInventoryContext } from "../contexts/InventoryContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/ReportModal.css";

const ViewReportModal = ({ onClose }) => {
  const {
    selectedReport,
    tasks,
    setTasks,
    parts,
    setParts,
    fetchTasksByReport,
    fetchPartsByReport,
  } = useReportContext();
  const { getVehicleInfoByVehicleId } = useVehicleContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();
  const { inventory, taskTemplate } = useInventoryContext();

  const getTaskById = (taskTemplateId) => {
    const task = taskTemplate.find((item) => item.id === taskTemplateId);
    if (!task) return;
    return task;
  };
  const getPartById = (partId) => {
    const task = inventory.find((item) => item.id === partId);
    if (!task) return;
    return task;
  };

  useEffect(() => {
    // Set tasks and parts data
    const fetchData = async () => {
      if (selectedReport) {
        const tasksData = await fetchTasksByReport(selectedReport.id);
        const partsData = await fetchPartsByReport(selectedReport.id);

        setTasks(tasksData);
        setParts(partsData);
      }
    };
    fetchData();
  }, [selectedReport, fetchTasksByReport, fetchPartsByReport]);

  return (
    <div className="modal-container">
      <div className="modal-card">
        <svg
          onClick={onClose}
          className="modal-card-close"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
        >
          <path
            width="100%"
            height="100%"
            d="M11.414 10l2.829-2.828a1 1 0 1 0-1.415-1.415L10 8.586 7.172 5.757a1 1 0 0 0-1.415 1.415L8.586 10l-2.829 2.828a1 1 0 0 0 1.415 1.415L10 11.414l2.828 2.829a1 1 0 0 0 1.415-1.415L11.414 10zM10 20C4.477 20 0 15.523 0 10S4.477 0 10 0s10 4.477 10 10-4.477 10-10 10z"
          />
        </svg>
        <h2>View Report</h2>
        <form className="modal-form">
          <label>
            Vehicle:
            <select name="vehicle" disabled>
              <option value="">
                {getVehicleInfoByVehicleId(selectedReport.vehicle).__str__}
                &nbsp;-&nbsp;
                {getOwnerNameByVehicleId(selectedReport.vehicle)}
              </option>
            </select>
          </label>

          <label>
            Status:
            <select name="status" disabled>
              <option value="">{selectedReport.status}</option>
            </select>
          </label>

          <label>
            Remarks:
            <textarea name="remarks" value={selectedReport.remarks} disabled />
          </label>

          <h3>Repairs Tasks</h3>
          {tasks.length > 0 && (
            <ul className="repair-list">
              {tasks.map((task, index) => (
                <li key={index}>
                  <p>
                    {getTaskById(task.task_template)?.name} - €
                    {getTaskById(task.task_template)?.price}
                  </p>
                </li>
              ))}
            </ul>
          )}
          {(tasks.length = 0 && <p>This report has no associated tasks.</p>)}

          <h3>Repairs Parts</h3>
          {parts.length > 0 && (
            <ul className="repair-list">
              {parts.map((part, index) => (
                <li key={index}>
                  <p>
                    {getPartById(part.part)?.name} - €
                    {getPartById(part.part)?.unit_price}
                  </p>
                </li>
              ))}
            </ul>
          )}
          {(parts.length = 0 && <p>This report has no associated parts.</p>)}

          <div className="button-group">
            <button type="button" className="cancel-btn" onClick={onClose}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ViewReportModal;
