import { Toast } from "./swal";

const withSuccessAlert = (fn, successMessage) => {
  return async (...args) => {
    const result = await fn(...args);
    if (result) {
      Toast.fire({
        icon: "success",
        title: successMessage,
      });
    }
    return result;
  };
};

export default withSuccessAlert;
