import React, { useState } from "react";
import Modal from "../components/Modal";
import { useAuth } from "../contexts/AuthContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useInventoryContext } from "../contexts/InventoryContext";
import { Toast } from "../utils/sweetalert";

const CreateReportModal = ({ onClose, onCreate }) => {
  const { user } = useAuth();
  const { vehicles } = useVehicleContext();
  const { inventory } = useInventoryContext(); // Assuming this provides a list of available parts.

  const [formData, setFormData] = useState({
    vehicle_id: "",
    created_by: user.id,
    status: "Pending",
    remarks: "",
    repairs: [],
  });

  const [selectedPart, setSelectedPart] = useState("");
  const [partCost, setPartCost] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRepairChange = (e) => {
    const part = inventory.find((item) => item.id === e.target.value);
    setSelectedPart(e.target.value);
    setPartCost(part ? part.price : "");
  };

  const addRepair = () => {
    if (!selectedPart) {
      Toast.fire("Error", "Please select a repair part.", "error");
      return;
    }

    const part = inventory.find((item) => item.id === selectedPart);
    if (!part) return;

    setFormData({
      ...formData,
      repairs: [
        ...formData.repairs,
        { part_id: part.id, description: part.name, total_cost: part.price },
      ],
    });

    setSelectedPart("");
    setPartCost("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.vehicle_id) {
      Toast.fire("Error", "Please select a vehicle.", "error");
      return;
    }
    onCreate(formData);
    onClose();
  };

  return (
    <Modal onClose={onClose}>
      <div className="auth-container">
        <div className="auth-card">
          <h2>Create Report</h2>
          <form className="auth-form" onSubmit={handleSubmit}>
            <label>
              Vehicle:
              <select
                name="vehicle_id"
                value={formData.vehicle_id}
                onChange={handleChange}
                required
              >
                <option value="">Select a vehicle</option>
                {vehicles.map((vehicle) => (
                  <option key={vehicle.id} value={vehicle.id}>
                    {vehicle.model} - {vehicle.license_plate}
                  </option>
                ))}
              </select>
            </label>

            <label>
              Status:
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                required
              >
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </label>

            <label>
              Remarks:
              <textarea
                name="remarks"
                value={formData.remarks}
                onChange={handleChange}
                placeholder="Enter remarks"
              />
            </label>

            <h3>Repairs</h3>
            <div className="repair-section">
              <select value={selectedPart} onChange={handleRepairChange}>
                <option value="">Select a repair part</option>
                {inventory.map((part) => (
                  <option key={part.id} value={part.id}>
                    {part.name} - €{part.price}
                  </option>
                ))}
              </select>
              <button type="button" onClick={addRepair}>
                Add Repair
              </button>
            </div>

            {formData.repairs.length > 0 && (
              <ul className="repair-list">
                {formData.repairs.map((repair, index) => (
                  <li key={index}>
                    {repair.description} - €{repair.total_cost}
                  </li>
                ))}
              </ul>
            )}

            <div className="button-group">
              <button type="submit">Create Report</button>
              <button type="button" className="cancel-btn" onClick={onClose}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </Modal>
  );
};

export default CreateReportModal;
