import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const { user } = useAuth();
  const axiosInstance = useAxios();
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all reports
  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("reports/");
      setReports(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      // Debug
      console.log("Sending report data:", reportData);

      const response = await axiosInstance.post("reports/", reportData);
      setReports((prevReports) => [...prevReports, response.data]);
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Success",
        text: "Report created successfully!",
        timer: 2000, // Auto-close after 2 seconds
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Update a report
  const updateReport = async (reportId, reportData) => {
    // Debug
    console.log("Updating report data:", reportData);

    try {
      const response = await axiosInstance.put(
        `reports/${reportId}/`,
        reportData
      );
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Updated",
        text: "Report updated successfully!",
        timer: 2000,
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    try {
      await axiosInstance.delete(`reports/${reportId}/`);
      setReports((prevReports) =>
        prevReports.filter((report) => report.id !== reportId)
      );
      // Success notification
      Swal.fire({
        icon: "success",
        title: "Deleted",
        text: "Report deleted successfully!",
        timer: 2000,
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Load reports when user logs in or changes
  useEffect(() => {
    if (user) fetchReports();
    else setReports([]); // Clear reports if no user
  }, [user]);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
