import { useUserContext } from "../contexts/UserContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import "../styles/FilterBar.css";

const FilterBar = ({ filterOptions, onFilterChange }) => {
  const { users } = useUserContext();
  const { owners } = useOwnerContext();
  const { reports } = useReportContext();
  const { vehicles } = useVehicleContext();

  // Extract unique report statuses
  const uniqueStatuses = new Map(
    reports.map((report) => [report.status, report.status_display])
  );

  // Get unique brands, models, and years from the vehicle list
  const uniqueBrands = [...new Set(vehicles.map((vehicle) => vehicle.brand))];
  const uniqueModels = [...new Set(vehicles.map((vehicle) => vehicle.model))];
  const uniqueYears = [...new Set(vehicles.map((vehicle) => vehicle.year))];
  // Get unique vehicle owners from the owners list
  const uniqueOwners = [
    ...new Set(owners.map((owner) => [owner.id, owner.full_name || "Unknown"])),
  ];

  return (
    <div className="filter-bar">
      {filterOptions.map((option) => (
        <div className="filter-group" key={option.name}>
          <label htmlFor={option.name}>{option.label}</label>
          {option.type === "select" ? (
            <select
              id={option.name}
              name={option.name}
              value={option.value}
              onChange={(e) => onFilterChange(option.name, e.target.value)}
            >
              <option value="">All {option.label}</option>
              {/* Filter bar options for the reports page */}
              {option.name === "vehicle" &&
                vehicles.map((vehicle) => (
                  <option key={vehicle.id} value={vehicle.id}>
                    {vehicle.__str__}
                  </option>
                ))}

              {option.name === "owner" &&
                owners.map((owner) => (
                  <option key={owner.id} value={owner.id}>
                    {owner.full_name}
                  </option>
                ))}
              {option.name === "created_at" &&
                // Extract unique and sorted dates
                [
                  ...new Set(
                    reports.map((report) => report.formatted_created_at)
                  ),
                ]
                  .sort((a, b) => new Date(a) - new Date(b))
                  .map((uniqueDate) => (
                    <option key={uniqueDate} value={uniqueDate}>
                      {uniqueDate}
                    </option>
                  ))}
              {option.name === "user" &&
                [...new Set(reports.map((report) => report.user))].map(
                  (userId) => {
                    const user = users.find((user) => user.id === userId);
                    return user ? (
                      <option key={user.id} value={user.id}>
                        {user.username}
                      </option>
                    ) : null;
                  }
                )}
              {option.name === "status" &&
                [...uniqueStatuses.entries()].map(([status, statusDisplay]) => (
                  <option key={status} value={status}>
                    {statusDisplay}
                  </option>
                ))}

              {/* Filter bar options for the owners page */}
              {option.name === "email" &&
                owners.map((owner) => (
                  <option key={owner.id} value={owner.email}>
                    {owner.email}
                  </option>
                ))}
              {/* Filter bar options for the vehicles page */}
              {option.name === "brand" &&
                uniqueBrands.map((brand) => (
                  <option key={brand} value={brand}>
                    {brand}
                  </option>
                ))}
              {option.name === "model" &&
                uniqueModels.map((model) => (
                  <option key={model} value={model}>
                    {model}
                  </option>
                ))}
              {option.name === "year" &&
                uniqueYears.map((year) => (
                  <option key={year} value={year}>
                    {year}
                  </option>
                ))}
              {option.name === "vehicle_owner" &&
                uniqueOwners.map(([ownerId, ownerName]) => (
                  <option key={ownerId} value={ownerId}>
                    {ownerName}
                  </option>
                ))}
            </select>
          ) : (
            <input
              type={option.type || "text"}
              id={option.name}
              name={option.name}
              value={option.value}
              onChange={(e) => onFilterChange(option.name, e.target.value)}
              placeholder={option.placeholder || ""}
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default FilterBar;
