import { useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";

const useCRUD = (apiEndpoint, parentResource = null, parentId = null) => {
  const axiosInstance = useAxios();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  console.log("CRUD id:", parentId);
  console.log("CRUD resource:", parentResource);

  // Construct URL dynamically
  const baseURL =
    parentResource && parentId
      ? `${parentResource}/${parentId}/${apiEndpoint}/`
      : `${apiEndpoint}/`;

  // Centralized error handling
  const handleError = (err) => {
    console.error(
      `Error in ${apiEndpoint}:`,
      err.message,
      err.response?.data || ""
    );
    setError(err.message || "An error occurred");
  };

  // Fetch all items
  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get(baseURL);
      setData(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create an item
  const createItem = async (itemData) => {
    try {
      const response = await axiosInstance.post(baseURL, itemData);
      setData((prev) => [...prev, response.data]);
      return response.data;
    } catch (err) {
      handleError(err);
    }
  };

  // Update an item
  const updateItem = async (itemId, updatedFields) => {
    try {
      const response = await axiosInstance.patch(
        `${apiEndpoint}/${itemId}/`,
        updatedFields
      );
      setData((prev) =>
        prev.map((item) => (item.id === itemId ? response.data : item))
      );
      return response.data;
    } catch (err) {
      handleError(err);
    }
  };

  // Delete an item
  const deleteItem = async (itemId) => {
    try {
      await axiosInstance.delete(`${apiEndpoint}/${itemId}/`);
      setData((prev) => prev.filter((item) => item.id !== itemId));
    } catch (err) {
      handleError(err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return {
    data,
    setData,
    fetchData,
    createItem,
    updateItem,
    deleteItem,
    loading,
    error,
  };
};

export default useCRUD;
