import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

const BASE_URL = "http://localhost:8000";

export const ClientContext = createContext();

export const ClientProvider = ({ children }) => {
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Helper function to get headers with token
  const getHeaders = () => {
    const token = localStorage.getItem("access_token");
    if (!token) {
      setError("User is not authenticated");
      return null;
    }
    return {
      Authorization: `Bearer ${token}`,
    };
  };

  // Fetch clients from the server
  const fetchClients = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/clients/`);
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  // Create a new client
  const createClient = async (clientData) => {
    try {
      const response = await axios.post(`${BASE_URL}/api/clients/`, clientData);
      setClients([...clients, response.data]);
    } catch (error) {
      console.error("Error creating client:", error);
    }
  };

  // Update a client
  const updateClient = async (clientId, updatedData) => {
    try {
      const response = await axios.put(
        `${BASE_URL}/api/clients/${clientId}/`,
        updatedData
      );
      setClients(clients.map((r) => (r.id === clientId ? response.data : r)));
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  // Delete a client
  const deleteClient = async (clientId) => {
    try {
      await axios.delete(`${BASE_URL}/api/clients/${clientId}/`);
      setClients(clients.filter((r) => r.id !== clientId));
    } catch (error) {
      console.error("Error deleting client:", error);
    }
  };

  useEffect(() => {
    fetchClients(); // Fetch clients when the component mounts
  }, []);

  return (
    <ClientContext.Provider
      value={{
        clients,
        selectedClient,
        setSelectedClient,
        fetchClients,
        createClient,
        updateClient,
        deleteClient,
      }}
    >
      {children}
    </ClientContext.Provider>
  );
};
