import React, { useState } from "react";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Vehicles.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useVehicleContext } from "../contexts/VehicleContext";

const Vehicles = () => {
  const { authenticatedUser } = useAuth();
  const {
    vehicles,
    selectedVehicle,
    setSelectedVehicle,
    createVehicle,
    updateVehicle,
    deleteVehicle,
    getVehicleInfoByVehicleId,
  } = useVehicleContext();

  const [filters, setFilters] = useState({
    brand: "",
    model: "",
    year: "",
    license_plate: "",
    owner: "",
  });

  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredVehicles = vehicles.filter((vehicle) => {
    return (
      (filters.brand === "" ||
        vehicle.brand.toLowerCase().includes(filters.brand.toLowerCase())) &&
      (filters.model === "" || vehicle.model.includes(filters.model)) &&
      (filters.year === "" || vehicle.year.includes(filters.year)) &&
      (filters.license_plate === "" ||
        vehicle.license_plate.includes(filters.license_plate)) &&
      (filters.owner === "" || vehicle.owner.includes(filters.owner))
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(true);
  };
  const handleEditClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newVehicle) => {
    createVehicle(newVehicle);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedVehicle) => {
    updateVehicle(selectedVehicle.id, updatedVehicle);
    setShowEditModal(false);
    setSelectedVehicle(null);
  };
  const handleDeleteConfirm = () => {
    deleteVehicle(selectedVehicle.id);
    setShowDeleteModal(false);
    setSelectedVehicle(null);
  };

  return (
    <div className="vehicles-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "brand",
            label: "Brand",
            value: filters.brand,
            type: "select",
          },
          {
            name: "model",
            label: "Model",
            value: filters.model,
            type: "select",
          },
          {
            name: "year",
            label: "Year",
            value: filters.year,
            type: "select",
          },
          {
            name: "license_plate",
            label: "License plate",
            value: filters.license_plate,
            placeholder: "Filter by License plate",
          },
          {
            name: "owner",
            label: "Owner",
            value: filters.owner,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Vehicles */}
      <div className="vehicles-list">
        {filteredVehicles.map((vehicle) => (
          <div
            key={vehicle.id}
            className="vehicle-card"
            title="View Vehicle"
            onClick={(e) => {
              // Prevent triggering view mode if clicking on an action button
              if (!e.target.closest(".vehicle-actions")) {
                handleViewClick(vehicle);
              }
            }}
          >
            <div className="vehicle-infos">
              <h3>{getVehicleInfoByVehicleId(vehicle)}</h3>
            </div>
            <div className="vehicle-actions">
              <button
                className="btn btn-green"
                onClick={() => handleEditClick(vehicle)}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={() => handleDeleteClick(vehicle)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
        {filteredVehicles.length === 0 && (
          <p>No vehicles match your filters.</p>
        )}
      </div>

      {/* Floating New Vehicle Button */}
      <button className="btn btn-float" onClick={handleCreateClick}>
        New Vehicle
      </button>

      {/* Create Owner Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new owner */}
          <form
            className="modal-form"
            onSubmit={(e) => {
              e.preventDefault();
              const newOwner = {
                name: e.target.name.value,
                email: e.target.email.value,
                phone: e.target.phone.value,
                address: e.target.address.value,
              };
              handleCreateSubmit(newOwner);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Owner</legend>
              <label>
                Name:
                <input type="text" name="name" placeholder="Enter owner name" />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  placeholder="Enter owner email address"
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  placeholder="Enter owner phone number"
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  placeholder="Enter owner address"
                />
              </label>
            </fieldset>
            <div className="button-group">
              <button className="btn btn-green" type="submit">
                Save
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Owner Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            className="modal-form"
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Owner</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  defaultValue={selectedOwner.full_name}
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  defaultValue={selectedOwner.email}
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  defaultValue={selectedOwner.phone}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  defaultValue={selectedOwner.address}
                />
              </label>
            </fieldset>
            <div className="button-group">
              <button
                className="btn btn-green"
                onClick={(e) => {
                  e.preventDefault();
                  handleEditClick(selectedOwner);
                }}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={(e) => {
                  e.preventDefault();
                  handleDeleteClick(selectedOwner);
                }}
              >
                Delete
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Edit Owner Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            className="modal-form"
            onSubmit={(e) => {
              e.preventDefault();
              const updatedOwner = {
                name: e.target.name.value,
                email: e.target.email.value,
                phone: e.target.phone.value,
                address: e.target.address.value,
              };
              handleEditSubmit(updatedOwner);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Owner</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  defaultValue={selectedOwner.full_name}
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  defaultValue={selectedOwner.email}
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  defaultValue={selectedOwner.phone}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  defaultValue={selectedOwner.address}
                />
              </label>
            </fieldset>
            <div className="button-group">
              <button className="btn btn-green" type="submit">
                Save
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Owner Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this owner?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Vehicles;
