import React, { createContext, useContext, useState, useEffect } from "react";
// Hooks
import useCRUD from "../hooks/useCRUD";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const {
    data: reports,
    createItem: createReport,
    updateItem: updateReport,
    deleteItem: deleteReport,
    loading,
    error,
  } = useCRUD("reports");

  const {
    data: tasks,
    createItem: createTask,
    updateItem: updateTask,
    deleteItem: deleteTask,
  } = useCRUD("tasks");

  const {
    data: parts,
    createItem: createPart,
    updateItem: updatePart,
    deleteItem: deletePart,
  } = useCRUD("parts");

  const [selectedReport, setSelectedReport] = useState(null);

  // Fetch tasks and parts when a report is selected
  useEffect(() => {
    if (!selectedReport) return;
    useCRUD(`reports/${selectedReport.id}/tasks`).fetchData();
    useCRUD(`reports/${selectedReport.id}/parts`).fetchData();
  }, [selectedReport]);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        tasks,
        setTasks,
        createTask,
        fetchTasksByReport,
        updateTask,
        deleteTask,
        parts,
        setParts,
        createPart,
        fetchPartsByReport,
        updatePart,
        deletePart,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
