import React, { useContext, useEffect } from "react";
import { ReportContext } from "../contexts/ReportContext";

const Reports = () => {
  const { reports, fetchReports } = useContext(ReportContext);

  useEffect(() => {
    fetchReports(); // Load reports when the component mounts
  }, [fetchReports]);

  return (
    <>
      <Topbar />
      <section className="reports">
        <h1>Reports</h1>
        {reports.length === 0 ? (
          <p>No reports available. Please create a new report.</p>
        ) : (
          <select>
            {reports.map((report) => (
              <option key={report.id} value={report.id}>
                {report.title}
              </option>
            ))}
          </select>
        )}
      </section>
    </>
  );
};

export default Reports;
