import React, { useState } from "react";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Owners.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useOwnerContext } from "../contexts/OwnerContext";

const Owners = () => {
  const { authenticatedUser } = useAuth();
  const {
    owners,
    selectedOwner,
    setSelectedOwner,
    createOwner,
    updateOwner,
    deleteOwner,
  } = useOwnerContext();

  const [filters, setFilters] = useState({
    name: "",
    email: "",
  });

  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredOwners = owners.filter((owner) => {
    return (
      (filters.name === "" ||
        owner.name.toLowerCase().includes(filters.name.toLowerCase())) &&
      (filters.email === "" || owner.email.includes(filters.email))
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (owner) => {
    setSelectedOwner(owner);
    setShowViewModal(true);
  };
  const handleEditClick = (owner) => {
    setSelectedOwner(owner);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (owner) => {
    setSelectedOwner(owner);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newOwner) => {
    createOwner(newOwner);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedOwner) => {
    updateOwner(selectedOwner.id, updatedOwner);
    setShowEditModal(false);
    setSelectedOwner(null);
  };
  const handleDeleteConfirm = () => {
    deleteOwner(selectedOwner.id);
    setShowDeleteModal(false);
    setSelectedOwner(null);
  };

  return (
    <div className="owners-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "name",
            label: "Name",
            value: filters.name,
            placeholder: "Filter by name",
          },
          {
            name: "email",
            label: "Email",
            value: filters.email,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Owners */}
      <div className="owners-list">
        {filteredOwners.map((owner) => (
          <div key={owner.id} className="owner-card">
            <div className="owner-infos">
              <h3>{owner.name}</h3>
              <p>
                <strong>Email:</strong>&nbsp;{owner.email}
              </p>
              <p>
                <strong>Phone:</strong>&nbsp;{owner.phone}
              </p>
              <p>
                <strong>Address:</strong>&nbsp;
                {owner.address}
              </p>
            </div>
            <div className="owner-actions">
              <button
                className="btn btn-blue"
                onClick={() => handleViewClick(owner)}
              >
                View
              </button>
              <button
                className="btn btn-green"
                onClick={() => handleEditClick(owner)}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={() => handleDeleteClick(owner)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
        {filteredOwners.length === 0 && <p>No owners match your filters.</p>}
      </div>

      {/* Floating New Owner Button */}
      <button className="btn-float" onClick={handleCreateClick}>
        New Owner
      </button>

      {/* Create Owner Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new owner */}
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const newOwner = {
                name: e.target.name.value,
                email: e.target.email.value,
                phone: e.target.phone.value,
                address: e.target.address.value,
              };
              handleCreateSubmit(newOwner);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Owner</legend>
              <label>
                Name:
                <input type="text" name="name" placeholder="Enter owner name" />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  placeholder="Enter owner email address"
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  placeholder="Enter owner phone number"
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  placeholder="Enter owner address"
                />
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Owner Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Owner</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  defaultValue={selectedOwner.name}
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  defaultValue={selectedOwner.email}
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  defaultValue={selectedOwner.phone}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  defaultValue={selectedOwner.address}
                />
              </label>
            </fieldset>
            <div className="form-actions">
              <button
                className="btn btn-green"
                onClick={(e) => {
                  e.preventDefault();
                  handleEditClick(selectedOwner);
                }}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={(e) => {
                  e.preventDefault();
                  handleDeleteClick(selectedOwner);
                }}
              >
                Delete
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowViewModal(false)}
              >
                Close
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Edit Owner Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const updatedOwner = {
                name: e.target.name.value,
                email: e.target.email.value,
                phone: e.target.phone.value,
                address: e.target.address.value,
              };
              handleEditSubmit(updatedOwner);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Edit Owner</legend>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  defaultValue={selectedOwner.name}
                />
              </label>
              <label>
                Email:
                <textarea
                  type="email"
                  name="email"
                  defaultValue={selectedOwner.email}
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  defaultValue={selectedOwner.phone}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  defaultValue={selectedOwner.address}
                />
              </label>
            </fieldset>
            <div className="form-actions">
              <button className="btn btn-green" type="submit">
                Save
              </button>
              <button
                className="btn btn-blue"
                type="button"
                onClick={() => setShowEditModal(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Delete Owner Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this owner?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Owners;
