import { useState } from "react";
import "../styles/CreateItemButton.css"; // Create a CSS file for styling

const CreateItemButton = ({ itemType, handleCreateClick }) => {
  const [visible, setVisible] = useState(true);

  return (
    <div className={`create-item-button-container${visible ? " show" : ""}`}>
      <button
        title={`Create new ${itemType}`}
        type="button"
        className="create-item-button"
        onClick={handleCreateClick}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="currentColor"
          className="icon"
        >
          <path
            d="M12 22.4199C17.5228 22.4199 22 17.9428 22 12.4199C22 6.89707 17.5228 2.41992 12 2.41992C6.47715 2.41992 2 6.89707 2 12.4199C2 17.9428 6.47715 22.4199 12 22.4199Z"
            strokeWidth="1.5"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </div>
  );
};

export default CreateItemButton;
