import React, { useState } from "react";
// Components
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
// Styles
import "../styles/Vehicles.css";
// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";

const Vehicles = () => {
  const { authenticatedUser } = useAuth();
  const {
    vehicles,
    selectedVehicle,
    setSelectedVehicle,
    createVehicle,
    updateVehicle,
    deleteVehicle,
    getVehicleInfoByVehicleId,
  } = useVehicleContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();

  const [filters, setFilters] = useState({
    brand: "",
    model: "",
    year: "",
    license_plate: "",
    vehicle_owner: "",
  });

  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredVehicles = vehicles.filter((vehicle) => {
    return (
      (filters.brand === "" ||
        vehicle.brand.toLowerCase().includes(filters.brand.toLowerCase())) &&
      (filters.model === "" ||
        vehicle.model.toLowerCase().includes(filters.model.toLowerCase())) &&
      (filters.year === "" || vehicle.year.toString().includes(filters.year)) &&
      (filters.license_plate === "" ||
        vehicle.license_plate
          .toLowerCase()
          .includes(filters.license_plate.toLowerCase())) &&
      (filters.vehicle_owner === "" ||
        (vehicle.owner &&
          vehicle.owner.toString().includes(filters.vehicle_owner.toString())))
    );
  });

  const handleCreateClick = () => {
    setShowCreateModal(true);
  };
  const handleViewClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(true);
  };
  const handleEditClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowEditModal(true);
  };
  const handleDeleteClick = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowViewModal(false);
    setShowDeleteModal(true);
  };

  const handleCreateSubmit = (newVehicle) => {
    createVehicle(newVehicle);
    setShowCreateModal(false);
  };
  const handleEditSubmit = (updatedVehicle) => {
    updateVehicle(selectedVehicle.id, updatedVehicle);
    setShowEditModal(false);
    setSelectedVehicle(null);
  };
  const handleDeleteConfirm = () => {
    deleteVehicle(selectedVehicle.id);
    setShowDeleteModal(false);
    setSelectedVehicle(null);
  };

  return (
    <div className="vehicles-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "brand",
            label: "Brand",
            value: filters.brand,
            type: "select",
          },
          {
            name: "model",
            label: "Model",
            value: filters.model,
            type: "select",
          },
          {
            name: "year",
            label: "Year",
            value: filters.year,
            type: "select",
          },
          {
            name: "license_plate",
            label: "License plate",
            value: filters.license_plate,
            placeholder: "Filter by License plate",
          },
          {
            name: "vehicle_owner",
            label: "Owner",
            value: filters.owner,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Vehicles */}
      <div className="vehicles-list">
        {filteredVehicles.map((vehicle) => (
          <div
            key={vehicle.id}
            className="vehicle-card"
            title="View Vehicle"
            onClick={(e) => {
              // Prevent triggering view mode if clicking on an action button
              if (!e.target.closest(".vehicle-actions")) {
                handleViewClick(vehicle);
              }
            }}
          >
            <div className="vehicle-infos">
              <h3>{getVehicleInfoByVehicleId(vehicle.id)}</h3>
              <p>
                <strong>Year:</strong>&nbsp;{vehicle.year}
              </p>
              <p>
                <strong>Owner:</strong>&nbsp;
                {getOwnerNameByVehicleId(vehicle.id)}
              </p>
            </div>
            <div className="vehicle-actions">
              <button
                className="btn btn-green"
                onClick={() => handleEditClick(vehicle)}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={() => handleDeleteClick(vehicle)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
        {filteredVehicles.length === 0 && (
          <p>No vehicles match your filters.</p>
        )}
      </div>

      {/* Floating New Vehicle Button */}
      <button className="btn btn-float" onClick={handleCreateClick}>
        New Vehicle
      </button>

      {/* Create Vehicle Modal */}
      {showCreateModal && (
        <Modal onClose={() => setShowCreateModal(false)}>
          {/* Form for creating a new vehicle */}
          <form
            className="modal-form"
            onSubmit={(e) => {
              e.preventDefault();
              const newVehicle = {
                brand: e.target.brand.value,
                model: e.target.model.value,
                year: e.target.year.value,
                license_plate: e.target.license_plate.value,
                owner: e.target.owner.value,
              };
              handleCreateSubmit(newVehicle);
            }}
          >
            <fieldset className="form-inputs">
              <legend>Create New Vehicle</legend>
              <label>
                Brand:
                <input
                  type="text"
                  name="brand"
                  placeholder="Enter vehicle brand"
                />
              </label>
              <label>
                Model:
                <input
                  type="text"
                  name="model"
                  placeholder="Enter vehicle model"
                />
              </label>
              <label>
                Year:
                <input
                  type="text"
                  name="year"
                  placeholder="Enter vehicle year"
                />
              </label>
              <label>
                License plate:
                <input
                  type="text"
                  name="license_plate"
                  placeholder="Enter vehicle license plate"
                />
              </label>
              <label>
                Owner:
                <input
                  type="text"
                  name="owner"
                  placeholder="Enter vehicle owner"
                />
              </label>
            </fieldset>
            <div className="button-group">
              <button className="btn btn-green" type="submit">
                Save
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* View Vehicle Modal */}
      {showViewModal && (
        <Modal onClose={() => setShowViewModal(false)}>
          <form
            className="modal-form"
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <fieldset className="form-inputs" disabled>
              <legend>View Vehicle</legend>
              <label>
                Brand:
                <input
                  type="text"
                  name="brand"
                  defaultValue={selectedVehicle.brand}
                />
              </label>
              <label>
                Model:
                <textarea
                  type="email"
                  name="model"
                  defaultValue={selectedVehicle.model}
                />
              </label>
              <label>
                Year:
                <input
                  type="text"
                  name="year"
                  defaultValue={selectedVehicle.year}
                />
              </label>
              <label>
                License plate:
                <input
                  type="text"
                  name="license_plate"
                  defaultValue={selectedVehicle.license_plate}
                />
              </label>
              <label>
                Owner:
                <input
                  type="text"
                  name="owner"
                  defaultValue={selectedVehicle.owner}
                />
              </label>
            </fieldset>
            <div className="button-group">
              <button
                className="btn btn-green"
                onClick={(e) => {
                  e.preventDefault();
                  handleEditClick(setSelectedVehicle);
                }}
              >
                Edit
              </button>
              <button
                className="btn btn-red"
                onClick={(e) => {
                  e.preventDefault();
                  handleDeleteClick(setSelectedVehicle);
                }}
              >
                Delete
              </button>
            </div>
          </form>
        </Modal>
      )}

      {/* Edit Vehicle Modal */}

      {/* Delete Vehicle Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this vehicle?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Vehicles;
