import React, { useContext, useState, useEffect } from "react";
import { ReportContext } from "../contexts/ReportContext";
import "../styles/Topbar.css";

const Topbar = () => {
  const {
    reports,
    selectedReport,
    setSelectedReport,
    createReport,
    updateReport,
    deleteReport,
    fetchReports,
  } = useContext(ReportContext);

  const [newReportTitle, setNewReportTitle] = useState("");

  useEffect(() => {
    // Fetch reports when the component is mounted
    fetchReports();
  }, []);

  const handleCreateReport = () => {
    if (newReportTitle) {
      createReport({ title: newReportTitle, description: "New report" });
      setNewReportTitle("");
    }
  };

  const handleUpdateReport = () => {
    if (selectedReport) {
      updateReport(selectedReport.id, {
        ...selectedReport,
        title: "Updated Title",
      });
    }
  };

  const handleDeleteReport = () => {
    if (selectedReport) {
      deleteReport(selectedReport.id);
      setSelectedReport(null);
    }
  };

  return (
    <div className="topbar">
      <button onClick={handleCreateReport}>Create Report</button>
      <input
        type="text"
        placeholder="New Report Title"
        value={newReportTitle}
        onChange={(e) => setNewReportTitle(e.target.value)}
      />
      <select
        value={selectedReport?.id || ""}
        onChange={(e) =>
          setSelectedReport(
            reports.find((r) => r.id === parseInt(e.target.value))
          )
        }
      >
        <option value="" disabled>
          Select a report
        </option>
        {reports.map((report) => (
          <option key={report.id} value={report.id}>
            {report.title}
          </option>
        ))}
      </select>
      <button onClick={handleUpdateReport} disabled={!selectedReport}>
        Update
      </button>
      <button onClick={handleDeleteReport} disabled={!selectedReport}>
        Delete
      </button>
    </div>
  );
};

export default Topbar;
