import React, { createContext, useContext, useState, useEffect } from "react";
import authAxios from "../utils/useAxios"; // Centralized axios instance with token handling

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch reports
  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await authAxios.get("/api/reports/");
      setReports(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      const response = await authAxios.post("/api/reports/", reportData);
      setReports((prevReports) => [...prevReports, response.data]);
    } catch (err) {
      handleError(err);
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedFields) => {
    try {
      const response = await authAxios.put(
        `/api/reports/${reportId}/`,
        updatedFields
      );
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    try {
      await authAxios.delete(`/api/reports/${reportId}/`);
      setReports((prevReports) =>
        prevReports.filter((report) => report.id !== reportId)
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Fetch reports when the provider mounts, only if the user is authenticated
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    if (token) {
      fetchReports();
    }
  }, []);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        fetchReports,
        createReport,
        updateReport,
        deleteReport,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
