import { useState } from "react";
import "../styles/CreateItemButton.css"; // Create a CSS file for styling

const CreateItemButton = ({ itemType, handleCreateClick }) => {
  const [visible, setVisible] = useState(true);

  return (
    <div className={`create-item-button-container${visible ? " show" : ""}`}>
      <button
        type="button"
        className="create-item-button"
        onClick={handleCreateClick}
      >
        {itemType}
      </button>
    </div>
  );
};

export default CreateItemButton;
