import React, { useEffect, useState } from "react";
import "../styles/FilterBar.css";

const FilterBar = ({ filterOptions, onFilterChange, users, clients }) => {
  const [userOptions, setUserOptions] = useState([]);
  const [clientOptions, setClientOptions] = useState([]);

  useEffect(() => {
    // Assuming users and clients are passed down as props
    setUserOptions(users);
    setClientOptions(clients);
  }, [users, clients]);

  return (
    <div className="filter-bar">
      {filterOptions.map((option) => (
        <div className="filter-group" key={option.name}>
          <label htmlFor={option.name}>{option.label}</label>
          {option.type === "select" ? (
            <select
              id={option.name}
              name={option.name}
              value={option.value}
              onChange={(e) => onFilterChange(option.name, e.target.value)}
            >
              <option value="">Select {option.label}</option>
              {option.name === "created_by" &&
                userOptions.map((user) => (
                  <option key={user.id} value={user.id}>
                    {user.name}
                  </option>
                ))}
              {option.name === "client" &&
                clientOptions.map((client) => (
                  <option key={client.id} value={client.id}>
                    {client.name}
                  </option>
                ))}
            </select>
          ) : (
            <input
              type={option.type || "text"}
              id={option.name}
              name={option.name}
              value={option.value}
              onChange={(e) => onFilterChange(option.name, e.target.value)}
              placeholder={option.placeholder || ""}
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default FilterBar;
