const handleCreateSubmit = (newVehicle) => {
  createVehicle(newVehicle);
  setShowCreateModal(false);
};
const handleEditSubmit = (updatedVehicle) => {
  updateVehicle(selectedVehicle.id, updatedVehicle);
  setShowEditModal(false);
  setSelectedVehicle(null);
};

<Modal onClose={() => setShowCreateModal(false)}>
  {/* Form for creating a new vehicle */}
  <form
    className="modal-form"
    onSubmit={(e) => {
      e.preventDefault();
      const newVehicle = {
        brand: e.target.brand.value,
        model: e.target.model.value,
        year: e.target.year.value,
        license_plate: e.target.license_plate.value,
        owner: e.target.owner.value,
      };
      handleCreateSubmit(newVehicle);
    }}
  >
    <fieldset className="form-inputs">
      <legend>Create New Vehicle</legend>
      <label>
        Brand:
        <input type="text" name="brand" placeholder="Enter vehicle brand" />
      </label>
      <label>
        Model:
        <input type="text" name="model" placeholder="Enter vehicle model" />
      </label>
      <label>
        Year:
        <input type="text" name="year" placeholder="Enter vehicle year" />
      </label>
      <label>
        License plate:
        <input
          type="text"
          name="license_plate"
          placeholder="Enter vehicle license plate"
        />
      </label>
      <label>
        Owner:
        <input type="text" name="owner" placeholder="Enter vehicle owner" />
      </label>
    </fieldset>
    <div className="button-group">
      <button className="btn btn-green" type="submit">
        Save
      </button>
    </div>
  </form>
</Modal>;
