const InvoiceCard = ({ invoice }) => {
  return (
    <section
      key={invoice.id}
      className="card-invoice"
      title="Open invoice PDF"
      onClick={(e) => {
        // Download the invoice PDF file
      }}
    >
      <header>{invoice.invoice_number}</header>
      <div>
        <p>Issued: {invoice.formatted_issued_date}</p>
        <p>Total Cost: {invoice.total_cost} CHF</p>
      </div>
      <a href={invoice.pdf} target="_blank">
        {invoice.pdf ? invoice.invoice_number : "No file found"}
      </a>
    </section>
  );
};
export default InvoiceCard;
